public void delete()
{
    RHRMEmploymentBase rhrmEmploymentBase;
    ;

    if (isConfigurationkeyEnabled(configurationKeyNum(RHRM)))
        return;

    ttsbegin;

    rhrmEmploymentBase = RHRMEmploymentBase::construct(this.OperationType, this);

    if (! rhrmEmploymentBase.checkTblExist(this))
    {
        throw error("@GLS101992");
    }

    super();
    rhrmEmploymentBase.cancel(this);

    ttscommit;
}

