public boolean validateDelete()
{
    RPayHistory             rPayHistory;
    boolean                 ret;

    ;

    ret = super();

    if (ret)
    {
        select firstonly RecId from rPayHistory
            where rPayHistory.Number    == this.Number &&
                  rPayHistory.StartDate  > this.StartDate;

        if (rPayHistory.RecId)
        {
            ret = checkFailed("@GLS103605");
        }

        if ((select firstonly rPayTrans
                 where rPayTrans.Emplid    == this.Number &&
                       rPayTrans.PayPeriod >= this.StartDate).RecId)
        {
            ret = ret && checkFailed(strFmt("@RUP1834", this.Number, RHRMEmplTable::find(this.Number).name()));
        }

        if (this.OperationType == RHRMJournalType::Receive)
        {
            ret = ret && RHRMEmploymentBase::checkDismissOperation(this);
        }
    }

    return ret;
}

public boolean validateField(FieldId _p1)
{
    boolean             ret;
    RPayHistory         rPayHistoryLast;

    ret = super(_p1);

    if (ret)
    {
        switch (_p1)
        {
            case fieldNum(RPayHistory, EndDate):

                if (this.EndDate && (this.EndDate < this.StartDate))
                {
                     ret = checkFailed("@SYS21592");
                }
                break;
            case fieldNum(RPayHistory, StartDate):

                select maxof(StartDate) from rPayHistoryLast
                    where rPayHistoryLast.RecId  != this.RecId &&
                          rPayHistoryLast.Number == this.Number;

                if (rPayHistoryLast && (rPayHistoryLast.StartDate > this.StartDate))
                {
                    ret = checkFailed(strFmt("@RUP31", this.emplId(), RHRMEmplTable::find(this.emplId()).name()));
                }
                break;
        }
    }

    return ret;
}

public boolean validateWrite()
{
    boolean             ret = true;
    RPayHistory         rPayHistoryLast;

    if (this.RecId)
        return super();

    if (this.EndDate && (this.EndDate < this.StartDate))
        ret = ret && checkFailed("@SYS21592");

    if (this.HrmOrganizationId && ! RPayHRMOrganization::find(this.HrmOrganizationId).Active)
        ret = checkFailed(strFmt("@RUP1328", this.emplId(), RHRMEmplTable::emplId2Name(this.emplId())));

    select firstonly rPayHistoryLast
        where rPayHistoryLast.RecId     != this.RecId &&
              rPayHistoryLast.Number    == this.Number &&
              rPayHistoryLast.StartDate >= this.StartDate;

    if (rPayHistoryLast)
        ret = ret && checkFailed(strFmt("@RUP31", this.emplId(), RHRMEmplTable::find(this.emplId()).name()));

    ret = ret & super();

    return ret;
}

