[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static boolean checkEmplWorkPeriod(RHRMEmplId _emplId,
                                                 StartDate  _startDate,
                                                 EndDate    _endDate,
                                                 boolean    _strictly = false)
{
    RPayHistory             rPayHistory;
    StartDate               recieveDate,
                            dismissDate;

    while select StartDate, OperationType from rPayHistory
        order by StartDate
        where rPayHistory.Number        == _emplId                            &&
              rPayHistory.StartDate     <= _endDate                           &&
             (rPayHistory.OperationType == RHRMJournalType::Receive           ||
              rPayHistory.OperationType == RHRMJournalType::Dismiss)
    {
        if (rPayHistory.OperationType == RHRMJournalType::Receive)
        {
            if (! _strictly || rPayHistory.StartDate <= _startDate)
                recieveDate = rPayHistory.StartDate;
        }

        if (rPayHistory.OperationType == RHRMJournalType::Dismiss)
        {
            if (rPayHistory.StartDate < _startDate)
            {
                recieveDate = dateNull();
                continue;
            }
            dismissDate = rPayHistory.StartDate;
        }

        if (recieveDate && dismissDate)
        {
            if (! _strictly                                            ||
                (rPayHistory.OperationType == RHRMJournalType::Dismiss &&
                rPayHistory.StartDate == _endDate))
            {
                return true;
            }
        }
    }
    if (recieveDate)
    {
        if (! _strictly || ! dismissDate || dismissDate == _endDate)
        {
            return true;
        }
    }
    return false;
}

[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static boolean exist(RHRMEmplId               _emplId,
                                   RpayHRMOrganizationId    _rhrmOrganizationId = '',
                                   StartDate                _startDate          = dateNull())
{
    boolean ok;

    if (_rhrmOrganizationId)
    {
        if (_startDate)
        {
            ok = _emplId && (select firstonly RecId from rPayHistory
                                where rPayHistory.Number                 == _emplId              &&
                                      rPayHistory.HrmOrganizationId      == _rhrmOrganizationId  &&
                                      rPayHistory.StartDate              == _startDate).RecId != 0;
        }
        else
        {
            ok = _emplId && (select firstonly RecId from rPayHistory
                                where rPayHistory.Number             == _emplId &&
                                      rPayHistory.HrmOrganizationId  == _rhrmOrganizationId).RecId != 0;
        }
    }
    else
    {
        if (_startDate)
        {
            ok = _emplId && (select firstonly RecId from rPayHistory
                                where rPayHistory.Number    == _emplId &&
                                      rPayHistory.StartDate == _startDate).RecId != 0;
        }
        else
        {
            ok = _emplId && (select firstonly RecId from rPayHistory
                                where rPayHistory.Number == _emplId ).RecId != 0;
        }
    }
    return ok;
}

[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static boolean existNetworkLastRetired(RHRMEmplId  _emplId)
{
    RPayHistory    rhrmNetworkHistoryDismiss;
    RPayHistory    rhrmNetworkHistoryLast;

    select maxof(StartDate) from rhrmNetworkHistoryDismiss
        where rhrmNetworkHistoryDismiss.Number          == _emplId &&
              rhrmNetworkHistoryDismiss.OperationType   == RHRMJournalType::Dismiss;

    select maxof(StartDate) from rhrmNetworkHistoryLast
        where rhrmNetworkHistoryLast.Number == _emplId;

    if (rhrmNetworkHistoryLast.StartDate > rhrmNetworkHistoryDismiss.StartDate)
        return false;

    return true;
}

[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static RPayHistory find(RHRMEmplId   _emplId,
                                      StartDate    _startDate = maxDate(),
                                      boolean      _forUpdate = false)
{
    RPayHistory  payHistory;

    if (_emplId)
    {
        payHistory.selectForUpdate(_forUpdate);

        select firstonly payHistory
            order by StartDate desc, TransTime desc
            where payHistory.Number    == _emplId &&
                  payHistory.StartDate <  _startDate;
    }

    return payHistory;
}

[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static RPayHistory findByRecId(RecId      _recId,
                                             boolean    _forUpdate = false)
{
    RPayHistory  payHistory;

    payHistory.selectForUpdate(_forUpdate);

    select firstonly payHistory
        where payHistory.RecId == _recId;

    return payHistory;
}

