[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static RPayHistory findLastOperation(RHRMEmplId                _emplId,
                                                   RHRMJournalType           _operationType,
                                                   StartDate                 _startDate = maxDate())
{
    RPayHistory       rPayHistory;

    if (_emplId)
    {
        select firstonly rPayHistory
            order StartDate desc, TransTime desc
            where rPayHistory.Number        == _emplId        &&
                  rPayHistory.OperationType == _operationType &&
                  rPayHistory.StartDate     <  _startDate;
    }

    return rPayHistory;
}

/// <summary>
/// Finds the last sign or move in employee’s events history for the period.
/// </summary>
/// <param name="_emplId">
/// The employee id.
/// </param>
/// <param name="_startDate">
/// The start date.
/// </param>
/// <param name="_excludeOrder">
/// Exclude this order if specified.
/// </param>
/// <returns>
/// Found latest employee’s events history table record.
/// </returns>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static RPayHistory findLastSignOnMove(RHRMEmplId _emplId       = "",
                                                    StartDate  _startDate    = maxDate(),
                                                    RefRecId   _excludeOrder = 0)
{
    RPayHistory       rPayHistory;

    select firstonly rPayHistory
        order StartDate desc, TransTime desc
        where (rPayHistory.Number          == _emplId                   ||
              (_emplId ? false : true))                                 &&
              rPayHistory.StartDate        <= _startDate                &&
              (rPayHistory.OperationType   == RHRMJournalType::Transfer ||
               rPayHistory.OperationType   == RHRMJournalType::Receive) &&
              (rPayHistory.OrderTransRecId != _excludeOrder             ||
               ! _excludeOrder);

    return rPayHistory;
}

[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static RPayHistory findOrderTransRecId(RefRecId  _recId,
                                                     boolean   _forupdate = false)
{
    RPayHistory  payHistory;

    if (_recId)
    {
        payHistory.selectForUpdate(_forupdate);
        select firstonly payHistory
            where payHistory.OrderTransRecId == _recId;
    }

    return payHistory;
}

[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static RPayHistory getHistoryLineByDate(RHRMEmplId   _emplId,
                                                      TransDate    _transDate      = RPayParameters::payPeriod(),
                                                      boolean      _allowForDelete = false)
{
    RPayHistory    rPayHistory;

    select firstonly rPayHistory order by StartDate desc, TransTime desc
        where rPayHistory.Number          == _emplId &&
              rPayHistory.StartDate       <= _transDate &&
              (rPayHistory.OperationType  == RHRMJournalType::Receive  ||
               rPayHistory.OperationType  == RHRMJournalType::Transfer ||
               (rPayHistory.OperationType == RHRMJournalType::Dismiss  &&
               (_allowForDelete ? true : false)));

    return rPayHistory;
}

[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static container getOrganizationByDateInterval(RHRMEmplId            _emplId,
                                                             TransDate             _startDate      = dateStartMth(RPayParameters::payPeriod(_emplId)),
                                                             TransDate             _endDate        = endmth(RPayParameters::payPeriod(_emplId)),
                                                             RpayHRMOrganizationId _organizationId = "")
{
    RPayHistory    rPayHistory;
    rPayHistory    prevPayHistory;
    container      con, conItem;
    TransDate      correctedPreviousEndDate;

    #define.StartDatePos(2)
    #define.EndDatePos(3)

    select firstonly rPayHistory
        order by StartDate desc, TransTime desc
        where rPayHistory.Number    == _emplId
           && rPayHistory.StartDate <=  _startDate
           && (rPayHistory.EndDate == dateNull()
              || rPayHistory.EndDate >= _startDate);

    if (rPayHistory
    && rPayHistory.OperationType != RHRMJournalType::Dismiss
    && rPayHistory.StartDate != _startDate
    && (_organizationId ? rPayHistory.HrmOrganizationId == _organizationId : true))
    {
        conItem =  [rPayHistory.HrmOrganizationId, _startDate,  dateNull(), rPayHistory.RecId];
        con     += [conItem];
    }

    while select StartDate, EndDate, OperationType, HrmOrganizationId, RecId, Number from rPayHistory
        order by StartDate, TransTime
        where rPayHistory.Number    == _emplId
           && rPayHistory.StartDate <= _endDate
           && ((rPayHistory.EndDate == dateNull()
             && rPayHistory.StartDate >= _startDate)
              || rPayHistory.EndDate > _startDate)
    {
        if (rPayHistory.operationType == RHRMJournalType::Dismiss
         && rPayHistory.StartDate >= _startDate
         && rPayHistory.StartDate <= _endDate
         && conLen(con) > 0)
        {
            conItem = conPeek(con, conLen(con));
            conItem = conPoke(conItem, #EndDatePos, rPayHistory.StartDate);
            con     = conPoke(con, conLen(con), conItem);
        }

        if (rPayHistory.OperationType != RHRMJournalType::Dismiss
         && (_organizationId ? rPayHistory.HrmOrganizationId == _organizationId : true)
         && rPayHistory.StartDate >= _startDate
         && rPayHistory.StartDate <= _endDate)
        {
            if (conLen(con))
            {
                correctedPreviousEndDate = rPayHistory.StartDate - 1;

                conItem = conPeek(con, conLen(con));
                if (conPeek(conItem,  #EndDatePos) == dateNull())
                {
                    conItem = conPoke(conItem, #EndDatePos, max(correctedPreviousEndDate, any2date(conPeek(conItem, #StartDatePos))));
                    con     = conPoke(con, conLen(con), conItem);
                }
            }
            //start date
            conItem =  [rPayHistory.HrmOrganizationId, rPayHistory.StartDate, dateNull(), rPayHistory.RecId];
            con     += [conItem];
        }

        if (rPayHistory.EndDate
         && rPayHistory.EndDate >= _startDate
         && rPayHistory.EndDate <= _endDate)
        {
            if (conLen(con))
            {
                conItem = conPeek(con, conLen(con));
                conItem = conPoke(conItem, #EndDatePos, rPayHistory.EndDate);
                con     = conPoke(con, conLen(con), conItem);
            }
            //end date
            prevPayHistory = rPayHistory::findLast(rPayHistory.Number, rPayHistory.StartDate);
            conItem =  [prevPayHistory.HrmOrganizationId, min(rPayHistory.EndDate + 1, _endDate), dateNull(), prevPayHistory.RecId];
            con     += [conItem];
        }
    }

    //set prev date for last container
    if (conLen(con))
    {
        if (conPeek(conPeek(con, conLen(con)), #EndDatePos) == dateNull()
            || any2date(conPeek(conPeek(con, conLen(con)), #StartDatePos))
                > any2date(conPeek(conPeek(con, conLen(con)), #EndDatePos)))
        {
            conItem = conPeek(con, conLen(con));
            conItem = conPoke(conItem, #EndDatePos, _endDate);
            con     = conPoke(con, conLen(con), conItem);
        }
    }
    return con;
}
