    /// <summary>
/// Increases the appropriate statistics field based on the <paramref name="_refType" /> parameter.
/// </summary>
/// <param name="_refType">
/// The type of the requirement transaction.
/// </param>
/// <param name="_isForecastPurch">
/// An extended <c>NoYes</c> enumeration value that specifies whether it is a forecast purchase.
/// </param>
public void addReqTrans(
    ReqRefType          _refType,
    ReqIsForecastPurch  _isForecastPurch
    )
{
    if (_isForecastPurch)
    {
        this.NumOfPurchForecast++;
    }

    switch (_refType)
    {
        case ReqRefType::InventOnHand       :
            this.NumOfInventOnhand++;
            break;

        case ReqRefType::Purch              :
            this.NumOfPurchLine++;
            break;

        case ReqRefType::Production         :
            this.NumOfProduction++;
            break;

        case ReqRefType::Kanban             :
            this.NumOfKanban++;
            break;

        case ReqRefType::Sales              :
            this.NumOfSalesLine++;
            break;

        case ReqRefType::ProdLine           :
            this.NumOfProdLine++;
            break;

        case ReqRefType::KanbanLine         :
            this.NumOfKanbanLine++;
            break;

        case ReqRefType::InventJournal      :
            this.NumOfInventJournal++;
            break;

        case ReqRefType::SalesForecast      :
            this.NumOfSalesForecast++;
            break;

        case ReqRefType::PmfPlannedProdBatch:
        //fallthrough to BOMPlannedOrder
        case ReqRefType::BOMPlannedOrder    :
            this.NumOfBOMPlannedOrder++;
            break;

        case ReqRefType::PlannedKanban      :
            this.NumOfPlannedKanban++;
            break;

        case ReqRefType::BOMLine            :
            this.NumOfBOMLine++;
            break;

        case ReqRefType::PlannedKanbanLine  :
            this.NumOfPlannedKanbanLine++;
            break;

        case ReqRefType::ItemPlannedOrder   :
            this.NumOfItemPlannedOrder++;
            break;

        case ReqRefType::TransferPlannedOrder   :
            this.NumOfTransferPlannedOrder++;
            break;

        case ReqRefType::PlannedIntercompanyDemand:
            this.NumOfPlannedIntercompanyDemand++;
            break;

        // Continuity master planning plugin
        case ReqRefType::MCRContinuity      :
            this.mcrNumofContinuity++;
            break;

        case ReqRefType::ConsignmentReplenishmentOrder :
            this.NumOfConsignmentReplenishmentOrder++;
            break;
    }
}

/// <summary>
/// Checks to see whether anything has been entered into the <c>Log</c> field.
/// </summary>
/// <returns>
/// true if anything has been entered into the <c>Log</c> field; otherwise, false.
/// </returns>
public display boolean hasLog()
{
    return this.Log != connull();
}

/// <summary>
///    Inserts a record and sets the percentages fields.
/// </summary>
public void insert()
{
    this.setPercentages();
    this.TimePeggingOptimization = this.calculateTimePeggingOptimization();
    super();
}

public void update()
{
    this.setPercentages();
    this.TimePeggingOptimization = this.calculateTimePeggingOptimization();
    super();
}

/// <summary>
///    Sets the <c>NumOfSalesLineFuturePct</c>, <c>NumOfPurchLineDecreasePct</c> and
///    <c>NumOfProductionDecreasePct</c> fields.
/// </summary>
public void setPercentages()
{
    this.NumOfSalesLineFuturePct    = this.NumOfSalesLine   ? this.NumOfSalesLineFuture / this.NumOfSalesLine * 100 : 0;
    this.NumOfPurchLineDecreasePct  = this.NumOfPurchLine   ? this.NumOfPurchLineDecrease / this.NumOfPurchLine * 100 : 0;
    this.NumOfProductionDecreasePct = this.NumOfProduction  ? this.NumOfProductionDecrease / this.NumOfProduction * 100 : 0;
    this.NumOfKanbanDecreasePct     = this.NumOfKanban      ? this.NumOfKanbanDecrease / this.NumOfKanban * 100 : 0;
}

