/// <summary>
/// Checks if a record with the specified record ID exists in the database.
/// </summary>
/// <param name="_recId">
/// The record ID to check.
/// </param>
/// <returns>
/// true if a record exists with the provided record ID; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    return _recId &&
           (select firstonly RecId from reqLog
                where reqLog.RecId == _recId).RecId != 0;
}

/// <summary>
/// Finds the specified record in the <c>ReqLog</c> table.
/// </summary>
/// <param name="_recId">
/// The record ID of the record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// The specified record if found; otherwise, an empty buffer.
/// </returns>
public static ReqLog find(
    RecId   _recId,
    boolean _forUpdate = false)
{
    ReqLog reqLog;

    if (_recId)
    {
        reqLog.selectForUpdate(_forUpdate);

        select firstonly reqLog
            where reqLog.RecId == _recId;
    }

    return reqLog;
}

public static boolean hasMPRLogsFromThePastSixMonths()
{
    ReqLog reqLog;

    select firstonly RecId from reqLog
        where reqLog.EndDateTime > DateTimeUtil::addMonths(DateTimeUtil::getSystemDateTime(), -6);

    return reqLog.RecId != 0;
}

private TimeHour24 calculateTimePeggingOptimization()
{
    const int MillisecondsPerSecond = 1000;
    return this.MillisecondsPeggingOptimization / millisecondsPerSecond;
}

/// <summary>
///    Inserts or updates a <c>ReqLog</c> record.
/// </summary>
/// <param name="_reqLog">
///    The log record to insert or update.
/// </param>
public static void insertUpdate(ReqLog _reqLog)
{
    ReqLog  reqLog;
    container log;
    container versionNumberCon;

    ttsbegin;
    select firstonly pessimisticlock reqLog where reqLog.ReqPlanId == _reqLog.ReqPlanId && reqLog.ProcessId == _reqLog.ProcessId;
    if (!reqLog)
    {
        buf2Buf(_reqLog, reqLog);
        reqLog.insert();
    }
    else
    {
        if (reqLog.StartDateTime > _reqLog.StartDateTime)
        {
            reqLog.StartDateTime = _reqLog.StartDateTime;
        }

        if (reqLog.EndDateTime  < _reqLog.EndDateTime)
        {
            reqLog.EndDateTime  = _reqLog.EndDateTime;
        }

        if (reqLog.TimeFutures  < _reqLog.TimeFutures)
        {
            reqLog.TimeFutures  = _reqLog.TimeFutures;
        }
        if (reqLog.TimeAction   < _reqLog.TimeAction)
        {
            reqLog.TimeAction   = _reqLog.TimeAction;
        }

        if (reqLog.TimeCoverage < _reqLog.TimeCoverage)
        {
            reqLog.TimeCoverage = _reqLog.TimeCoverage;
        }

        if (reqLog.TimeFinalization < _reqLog.TimeFinalization)
        {
            reqLog.TimeFinalization = _reqLog.TimeFinalization;
        }

        if (reqLog.NumOfItems   < _reqLog.NumOfItems)
        {
            reqLog.NumOfItems   = _reqLog.NumOfItems;
        }
        if (reqLog.TimeUpdate   < _reqLog.TimeUpdate)
        {
            reqLog.TimeUpdate   = _reqLog.TimeUpdate;
        }
        if (reqLog.NumOfLevels  < _reqLog.NumOfLevels)
        {
            reqLog.NumOfLevels  = _reqLog.NumOfLevels;
        }
        if (_reqLog.Comment)
        {
            reqLog.Comment = _reqLog.Comment;
        }
        if (_reqLog.CompleteUpdate)
        {
            reqLog.CompleteUpdate = _reqLog.CompleteUpdate;
        }
        if (_reqLog.ReqCalcUpdate)
        {
            reqLog.ReqCalcUpdate = _reqLog.ReqCalcUpdate;
        }
        if (reqLog.TimeCopy < _reqLog.TimeCopy)
        {
            reqLog.TimeCopy = _reqLog.TimeCopy;
        }
        if (reqLog.TimeAutoFirm < _reqLog.TimeAutoFirm)
        {
            reqLog.TimeAutoFirm = _reqLog.TimeAutoFirm;
        }
        if (reqLog.MaxChildThreads < _reqLog.MaxChildThreads)
        {
            reqLog.MaxChildThreads = _reqLog.MaxChildThreads;
        }
        if (reqLog.UsedChildThreads < _reqLog.UsedChildThreads)
        {
            reqLog.UsedChildThreads = _reqLog.UsedChildThreads;
        }
        if (_reqLog.Cancelled)
        {
            reqLog.Cancelled = _reqLog.Cancelled;
        }
        if ( reqLog.CopyDateTime < _reqLog.CopyDateTime)
        {
            reqLog.CopyDateTime = _reqLog.CopyDateTime;
        }
        if (reqLog.UsedTodaysdate < _reqLog.UsedTodaysdate)
        {
            reqLog.UsedTodaysdate = _reqLog.UsedTodaysdate;
        }
        if (_reqLog.Log)
        {
            log = _reqLog.Log;
            if (reqLog.Log)
            {
                versionNumberCon = conPeek(_reqLog.Log, 1);
                // Remove container which stores infolog version number
                if (typeOf(versionNumberCon) == Types::Container && conLen(versionNumberCon) == 1)
                {
                    log = conDel(_reqLog.Log, 1, 1);
                }
            }

            reqLog.Log += log;
        }

        reqLog.NumOfInventOnhand += _reqLog.NumOfInventOnhand;
        reqLog.NumOfPurchLine += _reqLog.NumOfPurchLine;
        reqLog.NumOfProduction += _reqLog.NumOfProduction;
        reqLog.NumOfProdLine += _reqLog.NumOfProdLine;
        reqLog.NumOfKanban += _reqLog.NumOfKanban;
        reqLog.NumOfKanbanLine += _reqLog.NumOfKanbanLine;
        reqLog.NumOfSalesLine += _reqLog.NumOfSalesLine;
        reqLog.NumOfInventJournal += _reqLog.NumOfInventJournal;
        reqLog.NumOfInventJournalIssue += _reqLog.NumOfInventJournalIssue;
        reqLog.NumOfInventJournalReceipt += _reqLog.NumOfInventJournalReceipt;
        reqLog.NumOfPurchForecast += _reqLog.NumOfPurchForecast;
        reqLog.NumOfSalesForecast += _reqLog.NumOfSalesForecast;
        reqLog.NumOfItemPlannedOrder += _reqLog.NumOfItemPlannedOrder;
        reqLog.NumOfBOMLine += _reqLog.NumOfBOMLine;
        reqLog.NumOfBOMPlannedOrder += _reqLog.NumOfBOMPlannedOrder;
        reqLog.NumOfPlannedKanban += _reqLog.NumOfPlannedKanban;
        reqLog.NumOfPlannedKanbanLine += _reqLog.NumOfPlannedKanbanLine;
        reqLog.NumOfTransferPlannedOrder += _reqLog.NumOfTransferPlannedOrder;
        reqLog.NumOfSalesLineFuture += _reqLog.NumOfSalesLineFuture;
        reqLog.NumOfPurchLineDecrease += _reqLog.NumOfPurchLineDecrease;
        reqLog.NumOfProductionDecrease += _reqLog.NumOfProductionDecrease;
        reqLog.NumOfKanbanDecrease += _reqLog.NumOfKanbanDecrease;
        reqLog.NumOfChanges += _reqLog.NumOfChanges;
        reqLog.NumOfSmmQuotationLine += _reqLog.NumOfSmmQuotationLine;
        reqLog.NumOfDeadLockRetry += _reqLog.NumOfDeadLockRetry;
        reqLog.NumOfUpdConflictRetry += _reqLog.NumOfUpdConflictRetry;
        ReqLog.NumOfUnhandledErrorRetry += _reqLog.NumOfUnhandledErrorRetry;
        reqLog.NumOfTransientSqlConnectionErrorRetry += _reqLog.NumOfTransientSqlConnectionErrorRetry;
        reqLog.NumOfAutoFirm += _reqLog.NumOfAutoFirm;
        reqLog.NumOfConsignmentReplenishmentOrder += _reqLog.NumOfConsignmentReplenishmentOrder;
        reqLog.MillisecondsPeggingOptimization += _reqLog.MillisecondsPeggingOptimization;
        reqLog.NumOfPeggingOptimizationAttempts += _reqLog.NumOfPeggingOptimizationAttempts;
        reqLog.NumOfPeggingOptimizationSuccessfulAttempts += _reqLog.NumOfPeggingOptimizationSuccessfulAttempts;
        reqLog.NumOfPeggingOptimizationSwaps += _reqLog.NumOfPeggingOptimizationSwaps;
        reqLog.MillisecondsCoverageInitLevel += _reqLog.MillisecondsCoverageInitLevel;
        reqLog.MillisecondsCoverageCoverage += _reqLog.MillisecondsCoverageCoverage;
        reqLog.MillisecondsCoverageCoCoverage += _reqLog.MillisecondsCoverageCoCoverage;
        reqLog.MillisecondsCoveragePartitionOrders += _reqLog.MillisecondsCoveragePartitionOrders;
        reqLog.MillisecondsCoverageScheduleResources += _reqLog.MillisecondsCoverageScheduleResources;
        reqLog.MillisecondsCoverageFinalizeLevel += _reqLog.MillisecondsCoverageFinalizeLevel;
        reqLog.MillisecondsFuturesInitLevel += _reqLog.MillisecondsFuturesInitLevel;
        reqLog.MillisecondsFuturesFuturesIssues += _reqLog.MillisecondsFuturesFuturesIssues;
        reqLog.MillisecondsFuturesPartitionOrders += _reqLog.MillisecondsFuturesPartitionOrders;
        reqLog.MillisecondsFuturesScheduleResources += _reqLog.MillisecondsFuturesScheduleResources;
        reqLog.MillisecondsAutoFirmingPartitionOrders += _reqLog.MillisecondsAutoFirmingPartitionOrders;
        reqLog.MillisecondsAutoFirmingScheduleResources += _reqLog.MillisecondsAutoFirmingScheduleResources;
        reqLog.MillisecondsAutoFirmingFinalizeLevel += _reqLog.MillisecondsAutoFirmingFinalizeLevel;
        reqLog.MillisecondsAutoFirmingFinishedScheduling += _reqLog.MillisecondsAutoFirmingFinishedScheduling;
        reqLog.MillisecondsPlanFinalizationBatchExpiryCalculation += _reqLog.MillisecondsPlanFinalizationBatchExpiryCalculation;

        reqLog.update();
    }
    ttscommit;
}

/// <summary>
/// Determines if concurrent deletes should throw exception.
/// </summary>
/// <returns>true if exception should be thrown; otherwise false.</returns>
public boolean shouldThrowExceptionOnZeroDelete()
{
    return super() || ReqEnforceConcurrentDeletesFlight::instance().isEnabled();
}

internal int numOfInputReceipts()
{
    return this.NumOfInventOnhand
        + this.NumOfPurchLine
        + this.NumOfProduction
        + this.NumOfInventJournalReceipt
        + this.NumOfKanban;
}

internal int numOfPlannedIssues()
{
    return this.NumOfBOMLine + this.NumOfPlannedKanbanLine + this.NumOfTransferPlannedOrder;
}

internal int numOfPlannedReceipts()
{
    return this.NumOfItemPlannedOrder
        + this.NumOfTransferPlannedOrder
        + this.NumOfBOMPlannedOrder
        + this.NumOfPlannedKanban;
}
