/// <summary>
/// Calculates the difference in field values.
/// </summary>
/// <param name="_fieldId">
/// The field ID number to be validated.
/// </param>
/// <returns>
/// A container indicating the difference of the field value.
/// </returns>
public container fieldValueIfDifferent(FieldId _fieldId)
{
    RetailAttributesLegalEntityLookup orig = this.orig();
    this.checkInvalidFieldAccess(false);
    orig.checkInvalidFieldAccess(false);

    if (orig.(_fieldId) != this.(_fieldId))
    {
        return [true, this.(_fieldId)];
    }
    else
    {
        return super(_fieldId);
    }
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;
    InventItemGroup inventItemGroup;
    ReqGroup reqGroup;
    InventCountGroup inventCountGroup;
    InventModelGroup inventModelGroup;
    TaxItemGroupHeading taxItemGroupHeading;
    InventSite inventSite;
    InventLocation inventLocation;

    changecompany(CompanyInfo::findRecId(this.LegalEntity).DataArea)
    {
        ret = super(_fieldIdToCheck);

        if (ret)
        {
            switch (_fieldIdToCheck)
            {
                case fieldNum(RetailAttributesLegalEntityLookup, GroupItemGroup):
                    if (this.GroupItemGroup)
                    {
                        inventItemGroup = InventItemGroup::find(this.GroupItemGroup);
                        if (!inventItemGroup)
                        {
                            ret = checkFailed("@RET260952");
                        }
                    }
                    break;
                case fieldNum(RetailAttributesLegalEntityLookup, GroupCoverage):
                    if (this.GroupCoverage)
                    {
                        reqGroup = ReqGroup::find(this.GroupCoverage);
                        if (!reqGroup)
                        {
                            ret = checkFailed("@RET260954");
                        }
                    }
                    break;
                case fieldNum(RetailAttributesLegalEntityLookup, GroupCounting):
                    if (this.GroupCounting)
                    {
                        inventCountGroup = InventCountGroup::find(this.GroupCounting);
                        if (!inventCountGroup)
                        {
                            ret = checkFailed("@RET260955");
                        }
                    }
                    break;
                case fieldNum(RetailAttributesLegalEntityLookup, GroupInventoryModel):
                    if (this.GroupInventoryModel)
                    {
                        inventModelGroup = InventModelGroup::find(this.GroupInventoryModel);
                        if (!inventModelGroup)
                        {
                            ret = checkFailed("@RET260956");
                        }
                    }
                    break;
                case fieldNum(RetailAttributesLegalEntityLookup, ItemSalesTaxGroupPurch):
                    if (this.ItemSalesTaxGroupPurch)
                    {
                        taxItemGroupHeading = TaxItemGroupHeading::find(this.ItemSalesTaxGroupPurch);
                        if (!taxItemGroupHeading)
                        {
                            ret = checkFailed("@RET260957");
                        }
                    }
                    break;
                case fieldNum(RetailAttributesLegalEntityLookup, ItemSalesTaxGroupSales):
                    if (this.ItemSalesTaxGroupSales)
                    {
                        taxItemGroupHeading = TaxItemGroupHeading::find(this.ItemSalesTaxGroupSales);
                        if (!taxItemGroupHeading)
                        {
                            ret = checkFailed("@RET260958");
                        }
                    }
                    break;
                case fieldNum(RetailAttributesLegalEntityLookup, SiteId):
                    if (this.SiteId)
                    {
                        inventSite = InventSite::find(this.SiteId);
                        if (!inventSite)
                        {
                            ret = checkFailed("@RET260959");
                        }
                    }
                    break;
                case fieldNum(RetailAttributesLegalEntityLookup, WarehousePurch):
                    if (this.WarehousePurch)
                    {
                        inventLocation = InventLocation::find(this.WarehousePurch);
                        if (!inventLocation)
                        {
                            ret = checkFailed("@RET260960");
                        }
                    }
                    break;
                case fieldNum(RetailAttributesLegalEntityLookup, WarehouseInvent):
                    if (this.WarehouseInvent)
                    {
                        inventLocation = InventLocation::find(this.WarehouseInvent);
                        if (!inventLocation)
                        {
                            ret = checkFailed("@RET260961");
                        }
                    }
                    break;
                case fieldNum(RetailAttributesLegalEntityLookup, WarehouseSales):
                    if (this.WarehouseSales)
                    {
                        inventLocation = InventLocation::find(this.WarehouseSales);
                        if (!inventLocation)
                        {
                            ret = checkFailed("@RET260953");
                        }
                    }
                    break;
                case fieldNum(RetailAttributesLegalEntityLookup, UnitPrice):
                    if (this.UnitPrice < 0)
                    {
                        ret = checkFailed("@RET260965");
                    }
                    break;
                case fieldNum(RetailAttributesLegalEntityLookup, UnitPriceSales):
                    if (this.UnitPriceSales < 0)
                    {
                        ret = checkFailed("@RET260966");
                    }
                    break;
                case fieldNum(RetailAttributesLegalEntityLookup, RetailPOSDisallowDiscount):
                    ret = this.validatePOSDisallowDiscount();
                    break;
                // <GBR>
                case fieldNum(RetailAttributesLegalEntityLookup, TaxServiceCode_BR):
                    if (BrazilParameters::isEnabled()
                        && this.TaxServiceCode_BR
                        && !TaxServiceCode_BR::findCompanyServiceCode(this.TaxServiceCode_BR))
                    {
                        ret = checkFailed("@GLS60420");
                    }
                    break;
                // </GBR>
                // <GEERU>
                case fieldNum(RetailAttributesLegalEntityLookup, LabelAttribute1):
                case fieldNum(RetailAttributesLegalEntityLookup, LabelAttribute2):
                case fieldNum(RetailAttributesLegalEntityLookup, LabelAttribute3):
                case fieldNum(RetailAttributesLegalEntityLookup, LabelAttribute4):
                case fieldNum(RetailAttributesLegalEntityLookup, LabelAttribute5):
                    ret = this.validateAttribute(_fieldIdToCheck);
                    break;
                // </GEERU>
            }
        }
    }

    return ret;
}

/// <summary>
/// Finds a specified record in the <c>RetailAttributesLegalEntityLookup</c> table.
/// </summary>
/// <param name="_recId">
/// The ID value of the retail attributes record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>RetailAttributesLegalEntityLookup</c> table; otherwise, an empty record.
/// </returns>
public static RetailAttributesLegalEntityLookup find(RecId _recId, boolean _forUpdate = false)
{
    RetailAttributesLegalEntityLookup attrLookup;
    attrLookup.selectForUpdate(_forUpdate);
    select firstonly attrLookup where attrLookup.RecId == _recId;
    return attrLookup;
}

/// <summary>
/// Finds the specified record in the <c>RetailAttributesLegalEntityLookup</c> table by using the
