public static RetailAttributesLegalEntityLookup findByCategory(EcoResCategoryId _ecoResCategoryId, LegalEntityRecId _recId, boolean _forUpdate = false)
{
    RetailAttributesLegalEntityLookup attrLookup;

    if (_ecoResCategoryId && _recId)
    {
        attrLookup.selectForUpdate(_forUpdate);
        select firstonly attrLookup where attrLookup.Category == _ecoResCategoryId && attrLookup.LegalEntity == _recId;
    }
    return attrLookup;
}

