/// <summary>
/// Finds the specified record with nonzero ReturnPolicy in the <c>RetailAttributesLegalEntityLookup</c> table.
/// </summary>
/// <param name="_ecoResCategoryId">
/// The category ID of the retail attributes record to search.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>RetailAttributesLegalEntityLookup</c> table; otherwise, an empty record.
/// </returns>
public static RetailAttributesLegalEntityLookup findReturnPolicyByCategory(EcoResCategoryId _ecoResCategoryId, boolean _forUpdate = false)
{
    RetailAttributesLegalEntityLookup attrLookup;
    EcoResCategory                    ecoResCategory, ecoResCategoryDerived;

    if (_ecoResCategoryId)
    {
        attrLookup.selectForUpdate(_forUpdate);

        select firstOnly RecId from ecoResCategory
            where ecoResCategory.RecId == _ecoResCategoryId
                join tableid from ecoResCategoryDerived
                order by ecoResCategoryDerived.NestedSetLeft desc
                where ecoResCategoryDerived.CategoryHierarchy == ecoResCategory.CategoryHierarchy
                   && ecoResCategory.NestedSetLeft            >= ecoResCategoryDerived.NestedSetLeft
                   && ecoResCategory.NestedSetLeft            <= ecoResCategoryDerived.NestedSetRight
                   && ecoResCategory.NestedSetRight           >= ecoResCategoryDerived.NestedSetLeft
                   && ecoResCategory.NestedSetRight           <= ecoResCategoryDerived.NestedSetRight
                join attrLookup
                    where attrLookup.Category == ecoResCategoryDerived.RecId
                       && attrLookup.ReturnPolicy
                       && attrLookup.LegalEntity == CompanyInfo::current();
    }
    return attrLookup;
}

/// <summary>
/// Inserts the Category instance value in the <c>EcoResCategoryInstanceValue</c> table.
/// </summary>
/// <param name="_categoryId">
/// The Category ID to search.
/// </param>
/// <remarks>
/// The <c>synchronizeInstance</c> method of <c>RetailAttributesLegalEntityLookup</c> table is called.
/// </remarks>
public static void synchronizeCategory(RefRecId _categoryId)
{
    RefRecId instanceValueId;
    CompanyInfo legalEntity;

    while select RecId, DataArea from legalEntity
    {
        changeCompany(legalEntity.DataArea)
        {
            instanceValueId = EcoResCategoryInstanceValue::insertCategoryInstance(_categoryId, legalEntity.RecId);
            RetailAttributesLegalEntityLookup::synchronizeInstance(instanceValueId);
        }
    }
}

/// <summary>
/// Inserts a record into the <c>RetailAttributesLegalEntityLookup</c> table with the specified
/// category instance.
/// </summary>
/// <param name="_instance">
/// The ID value indicating the category instance.
/// </param>
public static void synchronizeInstance(RefRecId _instance)
{
    RetailAttributesLegalEntityLookup attributeLookup;
    EcoResCategoryInstanceValue instanceValue;
    RetailProductAttributesLookup productAttributesLookup;

    attributeLookup = RetailAttributesLegalEntityLookup::findByInstance(_instance, true);
    if (!attributeLookup.RecId)
    {
        ttsbegin;
        attributeLookup.EcoResInstanceValue = _instance;
        instanceValue = EcoResCategoryInstanceValue::find(_instance);
        attributeLookup.LegalEntity = instanceValue.BuyingLegalEntity;
        attributeLookup.Category    = instanceValue.Category;
        attributeLookup.insert();
        ttscommit;
    }

    if (RetailProductAttributesLookup::synchronizeFields(attributeLookup, _instance))
    {
        ttsbegin;
        attributeLookup.IsSynchronized = NoYes::Yes;
        productAttributesLookup = attributeLookup;
        productAttributesLookup.selectForUpdate(true);
        attributeLookup.update();
        ttscommit;
    }
}

private boolean validateAttribute(FieldId _fieldIdToCheck)
{
    RefRecId    attribute;
    Query       query;
    QueryRun    queryRun;

    attribute = this.(_fieldIdToCheck);
    if (attribute)
    {
        query = RetailAttributesLegalEntityLookup::getAttributeQuery(this.Category, attribute);
        queryRun = new QueryRun(query);
        if  (!queryRun.next())
        {
            return checkFailed(strFmt("@SYP4881071", EcoResAttribute::find(attribute).Name));
        }
    }

    return true;
}

private static Query getAttributeQuery(EcoResCategoryId _category, RefRecId _attribute = 0)
{
    Query                       query = new Query();
    QueryBuildDataSource        qbdsEcoResAttribute;
    QueryBuildDataSource        qbdsEcoResCategoryAttributeLookup;
    QueryBuildDataSource        qbdsEcoResCategory;

    qbdsEcoResAttribute = query.addDataSource(tableNum(EcoResAttribute));
    if (_attribute)
    {
        qbdsEcoResAttribute.addRange(fieldNum(EcoResAttribute,RecId)).value(queryValue(_attribute));
    }

    qbdsEcoResCategoryAttributeLookup = qbdsEcoResAttribute.addDataSource(tableNum(EcoResCategoryAttributeLookup));
    qbdsEcoResCategoryAttributeLookup.relations(true);
    qbdsEcoResCategoryAttributeLookup.joinMode(JoinMode::InnerJoin);

    qbdsEcoResCategory = qbdsEcoResCategoryAttributeLookup.addDataSource(tableNum(EcoResCategory));
    qbdsEcoResCategory.relations(true);
    qbdsEcoResCategory.joinMode(JoinMode::InnerJoin);
    qbdsEcoResCategory.addRange(fieldNum(EcoResCategory, RecId)).value(queryValue(_category));

    return query;
}

