    this.RetailPOSDisallowManualDiscount = this.RetailPOSDisallowDiscount;

    return true;
}

/// <summary>
/// Updates return policy for current and child categories based on IsReturnPolicyInherited flag.
/// </summary>
/// <param name = "_category"> EcoresCategory </param>
internal static void synchronizeReturnPolicies(EcoResCategory _category, LegalEntityRecId _recId = 0)
{
    ProcCategoryExpanded procCategoryExpanded;
    RetailAttributesLegalEntityLookup retailAttributesLegalEntityLookup;
    RetailAttributesLegalEntityLookup ParentRetailAttributesLegalEntityLookup;

    while select ParentCategory, CategoryId, IsReturnPolicyInherited
         from procCategoryExpanded
            order by Level asc
            where procCategoryExpanded.NestedSetLeft >= _category.NestedSetLeft
            && procCategoryExpanded.NestedSetRight    <= _category.NestedSetRight
            && procCategoryExpanded.CategoryHierarchy == _category.CategoryHierarchy
            && procCategoryExpanded.IsReturnPolicyInherited == NoYes::Yes
    {
        if(_recId)
        {
            ttsbegin;

            select forupdate retailAttributesLegalEntityLookup
                where retailAttributesLegalEntityLookup.Category == procCategoryExpanded.CategoryId
                && retailAttributesLegalEntityLookup.LegalEntity == _recId;

            retailAttributesLegalEntityLookup.ReturnPolicy = RetailAttributesLegalEntityLookup::findByCategory(procCategoryExpanded.ParentCategory, _recId).ReturnPolicy;
            retailAttributesLegalEntityLookup.update();

            ttscommit;
        }
        else
        {
            ttsbegin;

            update_recordset retailAttributesLegalEntityLookup
                setting ReturnPolicy = ParentRetailAttributesLegalEntityLookup.ReturnPolicy
                where retailAttributesLegalEntityLookup.Category == procCategoryExpanded.CategoryId
                join parentRetailAttributesLegalEntityLookup
                where parentRetailAttributesLegalEntityLookup.Category == procCategoryExpanded.ParentCategory
                && parentRetailAttributesLegalEntityLookup.LegalEntity == retailAttributesLegalEntityLookup.LegalEntity;

            ttscommit;
        }
    }
}

/// <summary>
/// Synchronize return locations for current and child categories when IsReturnPolicyInherited field is modified.
/// </summary>
/// <param name = "_fieldId"> FieldId of the field modified. </param>
public void modifiedField(FieldId _fieldId)
{
    super(_fieldId);

    if (fieldNum(RetailAttributesLegalEntityLookup, ReturnPolicy) == _fieldId)
    {
        this.write();
        RetailAttributesLegalEntityLookup::synchronizeReturnPolicies(EcoResCategory::find(this.Category), this.LegalEntity);
    }
}

