/// <summary>
/// Finds the specified record in the <c>RetailFunctionalityProfile</c> table by using the specified
/// <c>parameter</c> value.
/// </summary>
/// <param name="_profileId">
/// The ID number of the profile record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>RetailFunctionalityProfile</c> table; otherwise, an empty record.
/// </returns>
public static RetailFunctionalityProfile find(RetailFunctionalityProfileId _profileId, boolean _forUpdate = false)
{
    RetailFunctionalityProfile functionalityProfile;

    if (_profileId)
    {
        functionalityProfile.selectForUpdate(_forUpdate);

        select firstonly functionalityProfile
            index hint profileIdx
            where functionalityProfile.profileId == _profileId;
    }

    return functionalityProfile;
}

/// <summary>
/// Validates the field with the specified identifier in <c>RetailFunctionalityProfile</c> table.
/// </summary>
/// <param name = "_fieldIdToCheck">The identifier of the field to be validated.</param>
/// <returns>true if validation condition met, else return false.</returns>
public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret=true;

    ret = super(_fieldIdToCheck);

    switch (_fieldIdToCheck)
    {
        case (fieldNum(RetailFunctionalityProfile, RequireSalesRep)):
            // When PromptForSalesRep is set to NoPrompt (0), then don't allow to RequireSalesRep to be NoYes::Yes.
            if ((this.PromptForSalesRep == RetailSalesRepPromptAtPOS::NoPrompt) && (this.RequireSalesRep == NoYes::Yes))
            {
                ret = checkFailed("@Retail:InvalidRequireSalesRep");
            }

            break;
        case (fieldNum(RetailFunctionalityProfile, showStaffListAtLogOn)):
        case (fieldNum(RetailFunctionalityProfile, staffBarcodeLogon)):
        case (fieldNum(RetailFunctionalityProfile, StaffBarcodeLogonRequiresPassword)):
        case (fieldNum(RetailFunctionalityProfile, staffCardLogon)):
        case (fieldNum(RetailFunctionalityProfile, StaffCardLogonRequiresPassword)):
        case (fieldNum(RetailFunctionalityProfile, limitStaffListToStore)):
            // When RetailEmployeeLogonType is set to  RetailEmployeeLogonType::AzureActiveDirectory, then don't allow showStaffListAtLogOn,
            // staffBarcodeLogon, StaffBarcodeLogonRequiresPassword, staffCardLogon, StaffCardLogonRequiresPassword, limitStaffListToStore to be NoYes::Yes.
            if((this.EmployeeLogonType == RetailEmployeeLogonType::AzureActiveDirectory ||
                this.EmployeeLogonType == RetailEmployeeLogonType::AzureActiveDirectorySingleSignOn) &&
                ((this.showStaffListAtLogOn == NoYes::Yes) ||
                (this.staffBarcodeLogon == NoYes::Yes) ||
                (this.StaffBarcodeLogonRequiresPassword == NoYes:: Yes) ||
                (this.staffCardLogon == NoYes::Yes) ||
                (this.StaffCardLogonRequiresPassword == NoYes::Yes) ||
                (this.limitStaffListToStore == NoYes::Yes)))
            {
                ret = checkFailed("@Retail:InvalidAssociatedLogonValuesForLogonTypeAzureActiveDirectory");
            }

            break;
        case (fieldNum(RetailFunctionalityProfile, notificationRefreshInterval)):
            // Notification time interval cannot be a negative value.
            if (this.notificationRefreshInterval < 0) 
            {
                ret = checkFailed("@Retail:NotificationRefreshIntervalInvalid");
            }
            break;
        case (fieldNum(RetailFunctionalityProfile, daysTransactionsExists)):
            // The number of days transactions can exists cannot be greater than 9999.
            if (this.daysTransactionsExists > 9999)
            {
                ret = checkFailed("@Retail:MaximumDaysTransactionsExistsError");
            }
            break;
        case fieldNum(RetailFunctionalityProfile, ShiftClosingInterval):
            ret = this.validateShiftClosingInterval();
            break;
        case fieldNum(RetailFunctionalityProfile, CreateOfflineCustomerOrders):
            ret = this.validateCustomerOrderCreationMode();
            break;
        case fieldNum(RetailFunctionalityProfile, CreateCustomerOrdersWithAsyncFallback):
            ret = this.validateCustomerOrderCreationMode();
            break;
        case fieldNum(RetailFunctionalityProfile, RequireAmountDeclaration):
            ret = this.validateRequireAmountDeclarationCanBeChecked();
            break;
        case fieldNum(RetailFunctionalityProfile, UseQuickPayShortcuts):
            // When DenominationsToDisplay is set to a value different than GreaterOrEqualToAmountDue, then don't allow UseQuickPayShortcuts to be NoYes::Yes.
            if(this.DenominationsToDisplay != RetailDenominationsToDisplay::GreaterOrEqualToAmountDue && this.UseQuickPayShortcuts == NoYes::Yes)
            {
                ret = checkFailed("@RetailUnifiedPaymentsInPOS:InvalidQuickPayShortcutsSelection");
            }
            break;
        case fieldNum(RetailFunctionalityProfile, OrderAttributeForTIN):
            ret = this.validateOrderAttributeForTIN();
            break;
        default:
            break;
    }

    return ret;
}

private boolean validateRequireAmountDeclarationCanBeChecked()
{
    boolean ret = true;

    // Before setting this to true, validate this functionality profile is set to any stores which dont have at least one payment method with paycash operation.
    if (this.RequireAmountDeclaration)
    {
        RetailStoreTable retailStoreTable;
        boolean cannotCheckDeclareAmounts;

        while select RecId 
            from retailStoreTable 
            where retailStoreTable.functionalityProfile == this.profileID
        {
            retailStoreTenderTypeTable retailStoreTenderTypeTable;

            select crosscompany count(RecId) from retailStoreTenderTypeTable
                where retailStoreTenderTypeTable.channel == retailStoreTable.RecId 
                && (retailStoreTenderTypeTable.posOperation == RetailPosOperationHelper::payCashOperation() 
                    || retailStoreTenderTypeTable.posOperation == RetailPosOperationHelper::payCashQuickOperation());

            if (!retailStoreTenderTypeTable.RecId)
            {
                cannotCheckDeclareAmounts = true;
                break;
            }
        }

        if (cannotCheckDeclareAmounts)
        {
            return checkFailed("@Retail:RequiredDeclarationCannotSetError");
        }
    }
    return ret;
}

private boolean validateCustomerOrderCreationMode()
{
    // CreateCustomerOrdersWithAsyncFallback cannot be enabled before enabling CreateOfflineCustomerOrders.
    if (this.CreateOfflineCustomerOrders == NoYes::No && this.CreateCustomerOrdersWithAsyncFallback == NoYes::Yes)
    {
        return checkFailed(strFmt("@Retail:CannotEnableCreateCustomerOrdersWithAsyncFallbackWithoutEnablingCreateOfflineCustomerOrders", "@Retail:CreateCustomerOrdersWithAsyncFallbackLabel", "@Retail:CreateOfflineCustomerOrders"));
    }

    return true;
}

private boolean validateOrderAttributeForTIN()
{
    boolean ret = true;

    if (this.OrderAttributeForTIN)
    {
        EcoResAttributeType attributeType = EcoResAttributeType::findByAttribute(this.OrderAttributeForTIN);

        // Only attributes of text type can be set as order attribute for TIN.
        if (!AttributeDataTypeDiscriminator::checkAttributeDataTypeText(attributeType.DataType))
        {
            ret = checkFailed("@RetailTransactionFiscalCustomer:RetailOrderAttributeForTINNotTextTypeErrorMsg");
        }
    }

    return ret;
}

/// <summary>
/// Init values for <c>RetailFunctionalityProfile</c> table.
/// </summary>
public void initValue()
{
    super();

    this.aggregateItems = NoYes::Yes;
    this.aggregateItemsForPrinting = NoYes::Yes;
    this.SearchCustomersInAx = NoYes::Yes;
    this.CreateCustomerOrderWaitTimeout = 30;
    this.daysTransactionsExists = 30;
}

/// <summary>
/// Edit or get the value indicating whether sales order invoices is included.
/// </summary>
/// <param name = "_set">True if value is changed, false otherwise.</param>
/// <param name = "_value">The new value to be set.</param>
/// <returns>The value indicates that sales order invoices is included.</returns>
public edit NoYes editIncludeSalesOrderInvoices(boolean _set, NoYes _value)
{
    // Since we store the inverted value (exclude sales order invoices),
    // we also invert the setter and getter on this method.
    if (_set)
    {
        if (_value == NoYes::Yes)
        {
            this.ExcludeSalesOrderInvoices = NoYes::No;
        }
        else
        {
            this.ExcludeSalesOrderInvoices = NoYes::Yes;
        }
    }

    NoYes returnValue;

    if (this.ExcludeSalesOrderInvoices == NoYes::Yes)
    {
        returnValue = NoYes::No;
    }
    else
    {
        returnValue = NoYes::Yes;
    }

    return returnValue;
}

/// <summary>
/// Validate ShiftClosingInterval field value.
/// </summary>
/// <returns>True if value is valid.</returns>
private boolean validateShiftClosingInterval()
{
    boolean ret = true;
    int oneDayMinutes = 60 * 24;

    if (this.ShiftClosingInterval >= oneDayMinutes)
    {
        ret = checkFailed(strFmt("@RetailRestrictingShiftDuration:ShiftClosingIntervalTooLarge", oneDayMinutes));
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (!this.isAnyInvoiceTypesSelected() && this.IncludeCreditNoteInvoices == NoYes::Yes)
        {
            warning("@Ret_HF_217763:OnlyCreditNoteInvoiceSelectedWarning");
            ret = false;
        }
    }

    return ret;
}

/// <summary>
/// Check whether any of the invoice types are selected.
/// </summary>
/// <returns>True if any invoice type is selected; otherwise, false.</returns>
private boolean isAnyInvoiceTypesSelected()
{
    // IncludeCreditNoteInvoices enables other user to pay or receive invoice types with negative amount,
    // so it's not part of invoice types.
    return this.ExcludeSalesOrderInvoices == NoYes::No || this.IncludeFreeTextInvoices == NoYes::Yes
        || this.IncludeProjectInvoices == NoYes::Yes;
}

/// <summary>
/// Behaviors of modified field with the specified identifier in <c>RetailFunctionalityProfile</c> table.
/// </summary>
/// <param name = "_fieldId">The identifier of the field modified.</param>
public void modifiedField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case (fieldNum(RetailFunctionalityProfile, SearchCustomersInAx)):                
            if (this.SearchCustomersInAx == NoYes::No)
            {
                this.DefaultCustomerSearchMode = RetailCustomerSearchScope::Local;
            }
            break;
        case (fieldNum(RetailFunctionalityProfile, daysTransactionsExists)):
            if (this.daysTransactionsExists > 0 && !Box::yesNo(strFmt("@Retail:DaysTransactionsExistsUpdateConfirmation", this.daysTransactionsExists), DialogButton::No))
            {
                this.daysTransactionsExists = this.orig().daysTransactionsExists;
            }
            break;
    }
}

/// <summary>
/// Displays the effective ManualCalculateDiscounts value.
/// </summary>
/// <returns>Effective ManualCalculateDiscounts value</returns>
internal display RetailManualCalculateDiscountsType displayManualCalculateDiscounts()
{
    return RetailParameters::find().EnableDelayedPriceAndDiscountCalculation;
}
