/// <summary>
/// Method initializes the fields <c>EFTPassword</c> and <c>EFT</c> of the <c>RetailHardwareProfile</c> table.
/// </summary>
/// <remarks>
/// RetailHardwareProfile::MaskedPasswordValue method returns the masked password.
/// RetailHardwareProfile.setupDOFields method initializes the fields <c>EFTUserId</c>, <c>EFTPassword</c> and <c>EFTCompanyId</c> of the
/// <c>RetailHardwareProfile</c> table using the values on the <c>CreditCardMicrosoftSetup</c> table.
/// </remarks>
public void initValue()
{
    super();

    this.eftPassword = RetailHardwareProfile::maskedPasswordValue();

    this.eft = RetailExternalNone::None;
    this.ProfileVersion = RetailProfileVersion::AX7;
    this.setupDOFields();

    this.printerKeepConnectionOpen = NoYes::Yes;
    this.printer2KeepConnectionOpen = NoYes::Yes;
    this.lineDisplayKeepConnectionOpen = NoYes::Yes;
    this.drawerKeepConnectionOpen = NoYes::Yes;
    this.drawer2KeepConnectionOpen = NoYes::Yes;
}

/// <summary>
/// The method is overridden to modify the values in the fields of the table when the fields are edited.
/// </summary>
