/// <summary>
/// Method initializes the fields <c>EFTUserId</c>, <c>EFTPassword</c> and <c>EFTCompanyId</c> of the <c>RetailHardwareProfile</c> table using the
/// values on the <c>CreditCardMicrosoftSetup</c> table.
/// </summary>
/// <remarks>
/// CreditCardMicrosoftSetup::find method finds the specified record in the <c>CreditCardMicrosoftSetup</c> table.
/// CreditCardProcessorsSecurity::creditCardProcessorsAccessKey method gets the credit card processors access key.
/// </remarks>
void setupDOFields()
{
#CreditCard
    CreditCardAccountSetup creditCardAccountSetup;
    CreditCardPaymentProperties properties;
    CreditCardPaymentProperty property;

    const boolean Decrypt = false;

    // User has opted DO for EFT, copy DO settings from AX if it doesn't exsit in Hardware profile
    if ((this.eft == RetailExternalNone::PaymentSDK) && (this.eftUserId == "") && (this.eftCompanyId == ""))
    {
        creditCardAccountSetup = CreditCardAccountSetup::findActive();

        if (creditCardAccountSetup != null)
        {
            properties = CreditCardPaymentProperties::fromXmlString(creditCardAccountSetup.manageEncryptionForSecureMerchantProperties(Decrypt, ''));

            this.eftUserId = '';
            this.eftPassword = '';

            property = properties.find(Microsoft.Dynamics.Retail.PaymentSDK.Portable.Constants.GenericNamespace::get_MerchantAccount(), #CreditCardMerchantAccountPropertyName_ServiceId);
            this.eftCompanyId = property ? property.parmStringValue() : '';
        }
    }
}

/// <summary>
/// Method validates the device name fields in the <c>RetailHardwareProfile</c> table.
/// </summary>
/// <returns>
/// true if the field is valid; otherwise false.
/// </returns>
private boolean validateDeviceNames()
{
    boolean result = true;

    // Printer 1 name should not be empty if enabled.
    if (this.printer != RetailPeripheralType::None && this.printerDeviceName == "")
    {
        checkFailed(strFmt("@RET4040155", "@RET2492")); //"'%1 device name' must be filled in.", "Printer"
        result = false;
    }

    // Printer 2 name should not be empty if enabled.
    if (this.printer2 != RetailPeripheralType::None && this.printer2DeviceName == "")
    {
        checkFailed(strFmt("@RET4040155", "@RET4160053")); //"'%1 device name' must be filled in.", "Printer 2"
        result = false;
    }

    // If both printer 1 and printer 2 are enabled then their name should not be same.
    if (this.printer != RetailPeripheralType::None
        && this.printer2 != RetailPeripheralType::None
        && this.printerDeviceName == this.printer2DeviceName)
    {
        checkFailed(strFmt("@RET4040156", "@RET2492", "@RET4160053")); //"'%1 device name' and '%2 device name' must be different.", "Printer", "Printer 2"
        result = false;
    }

    // Line display name should not be empty if enabled.
    if (this.displayDevice != RetailOposNone::None && this.displayDeviceName == "")
    {
        checkFailed(strFmt("@RET4040155", "@RET2695")); //"'%1 device name' must be filled in.", "Line display"
        result = false;
    }

    // MSR name should not be empty if enabled.
    if (this.msr != RetailOposNone::None && this.msrDeviceName == "")
    {
        checkFailed(strFmt("@RET4040155", "@RET2618")); //"'%1 device name' must be filled in.", "MSR"
        result = false;
    }

    // Drawer 1 name should not be empty if enabled.
    if (this.Drawer != RetailDrawer::None && this.DrawerDeviceName == "")
    {
        checkFailed(strFmt("@RET4040155", "@RET2696")); //"'%1 device name' must be filled in.", "Drawer"
        result = false;
    }

    // Drawer 2 name should not be empty if enabled.
    if (this.Drawer2 != RetailDrawer::None  && this.Drawer2DeviceName == "")
    {
        checkFailed(strFmt("@RET4040155", "@RET4160054")); //"'%1 device name' must be filled in.", "Drawer 2"
        result = false;
    }

    // If both Drawer 1 and Drawer 2 are enabled then their names should not be same.
    if (this.Drawer != RetailDrawer::None
        && this.Drawer2 != RetailDrawer::None
        && this.DrawerDeviceName == this.Drawer2DeviceName)
    {
        checkFailed(strFmt("@RET4040156", "@RET2696", "@RET4160054")); //"'%1 device name' and '%2 device name' must be different.", "Drawer", "Drawer 2"
        result = false;
    }

    // Scanner 1 name should not be empty if enabled.
    if (this.scanner != RetailOposNone::None && this.scannerDeviceName == "")
    {
        checkFailed(strFmt("@RET4040155", "@RET2500")); //"'%1 device name' must be filled in.", "Scanner"
        result = false;
    }

    // Scanner 2 name should not be empty if enabled.
    if (this.scanner2 != RetailOposNone::None  && this.scanner2DeviceName == "")
    {
        checkFailed(strFmt("@RET4040155", "@RET4160052")); //"'%1 device name' must be filled in.", "Scanner 2"
        result = false;
    }

    // If both Scanner 1 and Scanner 2 are enabled then their names should not be same.
    if (this.scanner != RetailOposNone::None
            && this.scanner2 != RetailOposNone::None
            && this.scannerDeviceName == this.scanner2DeviceName)
    {
        checkFailed(strFmt("@RET4040156", "@RET2500", "@RET4160052")); //"'%1 device name' and '%2 device name' must be different.", "Scanner", "Scanner 2"
        result = false;
    }

    // Scale name should not be empty if enabled.
    if (this.scale != RetailOposNone::None && this.scaleDeviceName == "")
    {
        checkFailed(strFmt("@RET4040155", "@RET2672")); //"'%1 device name' must be filled in.", "Scale"
        result = false;
    }

    // Keylock name should not be empty if enabled.
    if (this.keylock != RetailOPOSLogin::None && this.keylockDeviceName == "")
    {
        checkFailed(strFmt("@RET4040155", "@RET2697")); //"'%1 device name' must be filled in.", "Keylock"
        result = false;
    }

    // PIN pad name should not be empty if enabled.
    if (this.pinPad != RetailPinPadType::None && this.pinPadDeviceName == "")
    {
        checkFailed(strFmt("@RET4040155", "@RET2464")); //"'%1 device name' must be filled in.", "PIN pad"
        result = false;
    }

    // Signature capture name should not be empty if enabled.
    if (this.sigCap != RetailOposNone::None && this.sigCapDeviceName == "")
    {
        checkFailed(strFmt("@RET4040155", "@RET261077")); //"'%1 device name' must be filled in.", "Signature capture"
        result = false;
    }

    return result;
}

/// <summary>
/// Method validate the fields in the <c>RetailHardwareProfile</c> table.
/// </summary>
/// <param name="fieldToVaidate">
/// The field ID of the fields to validate.
/// </param>
/// <returns>
/// true if the field is valid; otherwise false.
/// </returns>
public boolean validateField(FieldId fieldToVaidate)
{
    boolean ret = super(fieldToVaidate);

    if (ret && fieldExt2Id(fieldToVaidate) == fieldNum(RetailHardwareProfile, DualDisplayImageInterval))
    {
        ret = this.validateImageRotatorInterval();
    }

    if (ret && fieldToVaidate == fieldNum(RetailHardwareProfile, PinPad))
    {
        ret = this.validateTerminals();
    }
    if (ret && fieldExt2Id(fieldToVaidate) == fieldNum(RetailHardwareProfile, drawerDeviceName))
    {
        ret = this.validateDeviceName(this.drawerDeviceName);
    }
    if (ret && fieldExt2Id(fieldToVaidate) == fieldNum(RetailHardwareProfile, drawer2DeviceName))
    {
        ret = this.validateDeviceName(this.drawer2DeviceName);
    }

    return ret;
}

/// <summary>
/// Validates the <c>dualDisplayImageInterval</c> field in the <c>RetailHardwareProfile</c> table.
/// </summary>
/// <returns>
/// true if the field is valid; otherwise false.
/// </returns>
boolean validateImageRotatorInterval()
{
    boolean ret = true;

    if (this.DualDisplay == true
        && this.DualDisplayType == RetailDualDisplayTypeBase::ImageRotator
        && this.DualDisplayImageInterval < 1)
        {
            Box::info("@RET3002");
            ret = false;
        }

    return ret;
}

private boolean validateDeviceName(RetailDeviceName deviceName)
{
    str regex  = "^[a-zA-Z0-9-_]*$";
    boolean isValid = false;
    System.Text.RegularExpressions.Match isDeviceNameMatchesPattern;

    isDeviceNameMatchesPattern = System.Text.RegularExpressions.Regex::Match(deviceName, regex);

    isValid = isDeviceNameMatchesPattern.get_Success();

    return isValid;
}

private boolean validateTerminals()
{
    boolean ret = true;

    if (this.pinPad == RetailPinPadType::ManagedByEFTService)
    {
        if ((select firstOnly RecId from retailTerminalTable where
                retailTerminalTable.hardwareProfile == this.profileId &&
                !retailTerminalTable.EFTTenderTypeIdDefault).RecId)
        {
            ret = checkFailed("@SYS4007510");
        }
    }

    return ret;
}

/// <summary>
/// Method validates the record at the time of writing the record to the database.
/// </summary>
/// <returns>
/// true if the record is valid; otherwise false.
/// </returns>
public boolean validateWrite()
{
    boolean ret = super();

    if (ret)
    {
        ret = this.validateImageRotatorInterval() && this.validateDeviceNames();
    }

    return ret;
}

