/// <summary>
/// Validates that the specified record exists in the <c>RetailHardwareProfile</c> table; if not, an error message is displayed.
/// </summary>
/// <param name="_profileId">
/// The profile number of the POS hardware profiles record to check for existence.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
static boolean checkExist(RetailHardwareProfileId _profileId)
{
    if (_profileId && !RetailHardwareProfile::exist(_profileId))
    {
        return checkFailed(strFmt(RetailHardwareProfile::txtNotExist(), _profileId));
    }
    return true;
}

/// <summary>
/// Indicates whether the specified record in the <c>RetailHardwareProfile</c> table exists.
/// </summary>
/// <param name="_profileId">
/// The profile number of the POS <c>RetailHardwareProfile</c> record to check for existence.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
static boolean exist(RetailHardwareProfileId _profileId)
{
    return _profileId && (select firstonly RecId from retailHardwareProfile
                          index hint profileIdx
                          where retailHardwareProfile.ProfileId == _profileId).RecId != 0;
}

/// <summary>
/// Finds the specified record in the <c>RetailHardwareProfile</c> table by using the specified
/// <c>parameter</c> value.
/// </summary>
/// <param name="_profileId">
/// The profile number of the POS <c>RetailHardwareProfile</c> record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>RetailHardwareProfile</c> table; otherwise, an empty record.
/// </returns>
public static RetailHardwareProfile find(RetailHardwareProfileId _profileId, boolean _forUpdate = false)
{
    RetailHardwareProfile  hardwareProfile;

    if (_profileId)
    {
        hardwareProfile.selectForUpdate(_forUpdate);

        select firstonly hardwareProfile
            index hint profileIdx
            where hardwareProfile.ProfileId == _profileId;
    }

    return hardwareProfile;
}

