{
    return _fieldId == fieldNum(RetailHardwareProfile, SecureMerchantProperties) ? CreditCardPaymentProperties::CryptoPurpose : super(_fieldId);
}

	/// <summary>
    /// Get the hash of the merchant properties.
    /// If the hash has not been computed yet, this is first computed and then sent.
    /// </summary>
    /// <returns>The hash of the merchant properties.</returns>
    public str getMerchantPropertiesHash()
    {
        RetailHardwareProfile hardwareProfile;
        str result = this.MerchantPropertiesHash;

        // If the hash has not yet been computed try compute it.
        if (System.String::IsNullOrWhiteSpace(result))
        {
            hardwareProfile = RetailHardwareProfile::find(this.profileId, true);

            if (hardwareProfile.RecId)
            {
                ttsbegin;
                hardwareProfile.update();

                ttscommit;
                result = hardwareProfile.MerchantPropertiesHash;
            }
        }

        return result;
    }

private void updateSelfServicePackage(RetailHardwareProfile hardwareProfile)
{
    RetailSharedParameters retailSharedParameters = RetailSharedParameters::find(true);
    if (hardwareProfile.SelfServicePackage == 0)
    {
        hardwareProfile.SelfServicePackage = retailSharedParameters.DefaultHardwarePeripheralSimulatorPkg;
    }
}

/// <summary>
/// Update field MergedSelfServicePackageReference to default package.
/// </summary>
private void updateToDefaultSelfServiceMergedPackage()
{
    // Update to default package only if the field MergedSelfServicePackageReference
    // does not have any value.
    if (this.MergedSelfServicePackageReference == 0)
    {
        RetailSelfServiceDefaultPackages defaultPackage = RetailSelfServiceDefaultPackages::findDefaultPackage(
            tableNum(RetailSelfServiceDefaultPackages), PackageType::HardwarePeripheralSimulator);

        this.MergedSelfServicePackageReference = defaultPackage.MergedSelfServicePackageReference;
    }
}

/// <summary>
/// Updates the EFT merchant properties hash column.
/// </summary>
private void updateMerchantPropertiesHash()
{
    str decryptedMerchantProperties = this.secureMerchantPropertiesEdit(false, '');
    str hashAlgorithm = RetailSharedParameters::find(false).StaffPasswordHash;
    str invariant = ' ', salt = ' ';

    this.MerchantPropertiesHash = RetailData::HashData2(decryptedMerchantProperties, hashAlgorithm, invariant, salt);
}
