public void initValue()
{
    super();

    // Enable parallel posting by default; maximum sales orders handled by each task is 100.
    this.ParallelSOPosting = NoYes::Yes;
    this.MaxSalesOrdersPerTask = 100;

    // EOD parallel posting configuration.
    this.MaxNumberOfStatementsPostInParallel = RetailEodStatementAppSuiteConstants::DefaultMaxNumberOfStatementsInParallel;
    this.MaxNumberOfThreadsCustomerOrder = RetailEodStatementAppSuiteConstants::DefaultMaxNumberOfThreadsCustomerOrder;

    this.GiftCardCompany = curext();
    this.MinimumPasswordLength = 8;
    this.RequireNumericChar = NoYes::Yes;
    this.DiscountAlgorithmMode = RetailDiscountAlgorithmMode::Marginal;
    this.MaxBestDealStepCount = 1000; // default value
    this.NumberOfPasswordsRemembered = 24;

    // Lockout duration (in minutes)
    this.LockoutDuration = 60;

    // Login counter reset duration (in minutes)
    this.LoginCounterResetDuration = 30;

    // Number of incorrect password attempts after which the user is locked out.
    this.LockoutThreshold = 5;

    // Number of days in which the password expires.
    this.PasswordExpiryInterval = 90;

    // For new Company, we enable this ShowOnlyCarrierOptionsForShipOrders by default
    // For existing Companies, the default value is No
    this.ShowOnlyCarrierOptionsForShipOrders = NoYes::Yes;

    // The maximum number of runtime tasks for the archive credit card transaction data batch header.
    this.PaymentBlobArchiveMaxTasks = 4;

    if (RetailUnifiedReturnsFeatureExposure::isEnabled())
    {
        this.AllowExchangeOnReturnOrders = NoYes::Yes;
    }

    if (RetailTaxInitTaxCalcBehaviorValueInRetailParameterCreateFlight::instance().isEnabled())
    {
        this.CustomerOrderTaxCalculationBehavior = RetailCustomerOrderTaxCalculationBehavior::DontRecalculate;
    }
}

