/// <summary>
/// Modifies the specified field to its original value.
/// </summary>
/// <param name="_fieldName">
/// The field name.
/// </param>
/// <param name="_arrayIndex">
/// The array index of the field; optional.
/// </param>
public void modifiedFieldValue(FieldName _fieldName, int _arrayIndex = 1)
{
    super(_fieldName, _arrayIndex);

    switch (_fieldName)
    {
        case fieldStr(RetailParameters, AggregateBeforePosting):
            if (!this.AggregateBeforePosting)
            {
                this.SkipAggregationForReturns = false;
                this.AggregateSalesReturns_RU = false;
            }
            break;

        case fieldStr(RetailParameters, SkipAggregationForReturns):
            if (this.SkipAggregationForReturns)
            {
                this.AggregateSalesReturns_RU = false;
            }
            break;

        case fieldStr(RetailParameters, ProcessGiftCardsAsPrepayments_RU):
            if (this.ProcessGiftCardsAsPrepayments_RU)
            {
                this.GiftCardClosingLedgerDimension = 0;
            }
            break;
    }
}

/// <summary>
/// Calls the <c>errorOnRename</c> method of the <c>RetailConnActionManagement</c> class.
/// </summary>
public void renamePrimaryKey()
{
    ;
    RetailConnActionManagement::errorOnRename(this);

    super();
}

    public boolean validateField(FieldId _fieldIdToCheck)
    {
        const int MaximumPasswordsRemembered = 24;
        const int MaximumGiftCardInquiryPrintHistoryCount = 10;
        container conCompanies;
        str liabilityCompanyParent;
        boolean ret;
        RetailParameters retailParameters = RetailParameters::find();
        ret = super(_fieldIdToCheck);

        switch (fieldExt2Id(_fieldIdToCheck))
        {
            case fieldNum(RetailParameters, ItemSalesStatisticsOn) :
                ret = this.validateItemSalesStatisticsOn();
                break;
            case fieldNum(RetailParameters, TotalDiscountLedgerDimension) :
                ret = MainAccount::checkTransactionAccount(this.TotalDiscountLedgerDimension);
                break;
            case fieldNum(RetailParameters, MultiBuyLedgerDimension) :
                ret = MainAccount::checkTransactionAccount(this.MultiBuyLedgerDimension);
                break;
            case fieldNum(RetailParameters, MixMatchLedgerDimension) :
                ret = MainAccount::checkTransactionAccount(this.MixMatchLedgerDimension);
                break;
            case fieldNum(RetailParameters, OfferLedgerDimension) :
                ret = MainAccount::checkTransactionAccount(this.OfferLedgerDimension);
                break;
            case fieldNum(RetailParameters, GiftCardCompany) :
                if (this.GiftCardCompany == "")
                {
                    // Validation for required field will validate
                    break;
                }

                if (this.GiftCardCompany == curext())
                {
                    break;
                }
                conCompanies = [this.GiftCardCompany];
                liabilityCompanyParent = RetailParameters::findCrossCompany(conCompanies).GiftCardCompany;
                if (liabilityCompanyParent != this.GiftCardCompany)
                {
                    warning("@REX4040075");
                    ret = false;
                }

                if (ret)
                {
                    ret = this.checkHasSameValueInAllGiftCardCompanies(fieldNum(RetailParameters, ProcessGiftCardsAsPrepayments_RU));
                    // both checks should be run even first one has failed
                    ret = this.checkHasSameValueInAllGiftCardCompanies(fieldNum(RetailParameters, UseGiftCardPolicies)) && ret;
                }

                break;
            case fieldNum(RetailParameters, GiftCardInquiryPrintHistoryCount) :
                if (this.GiftCardInquiryPrintHistoryCount < 0 || this.GiftCardInquiryPrintHistoryCount > MaximumGiftCardInquiryPrintHistoryCount)
                {
                    ret = checkFailed(strFmt("@Retail:RetailGiftCardInquiryPrintHistoryCountValueOutOfAllowedRange", 0, MaximumGiftCardInquiryPrintHistoryCount));
                }
                break;
            case fieldNum(RetailParameters, MinimumPasswordLength) :
                if (this.MinimumPasswordLength <= 0 || this.MinimumPasswordLength > 32)
                {
                    ret = checkFailed("@SYS4006639");
                }
                break;
            case fieldNum(RetailParameters, ProcessGiftCardsAsPrepayments_RU) :
                ret = this.checkHasSameValueInAllGiftCardCompanies(fieldExt2Id(_fieldIdToCheck));
                break;
            case fieldNum(RetailParameters, UseGiftCardPolicies) :
                ret = this.checkHasSameValueInAllGiftCardCompanies(fieldExt2Id(_fieldIdToCheck));
                break;
            case fieldNum(RetailParameters, NumberOfPasswordsRemembered) :
                if (this.NumberOfPasswordsRemembered < 0 || this.NumberOfPasswordsRemembered > MaximumPasswordsRemembered)
                {
                    ret = checkFailed(strFmt("@Retail:NumberOfPasswordsRememberedValueOutOfAllowedRange",0, MaximumPasswordsRemembered));
                }
                break;
            case fieldNum(retailParameters, UpdateGSTTransactionId_IN) :
                if (this.UpdateGSTTransactionId_IN == NoYes::Yes)
                {
                    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
                    {
                        RetailReceiptMasks receiptMask;
                        while select receiptMask
                            where receiptMask.ReceiptTransType == RetailReceiptTransaction::Sale
                                || receiptMask.ReceiptTransType == RetailReceiptTransaction::SalesOrder
                        {
                            if (!receiptMask.maskContainsStoreAndTerminalNumber())
                            {
                                ret = checkFailed("@RetailGTE:MessageWhenEnableUpdatePOSInvoiceNumberOption");
                                break;
                            }
                        }
                    }
                }
                break;
            case fieldNum(RetailParameters, MaxNumberOfThreadsCustomerOrder):
                if (this.MaxNumberOfThreadsCustomerOrder > RetailEodStatementAppSuiteConstants::MaximumNumberOfThreadsCustomerOrder)
                {
                    ret = checkFailed(strFmt("@RET_HF_264066:MaxNumberOfCustomerOrderThreadsExceeded", RetailEodStatementAppSuiteConstants::MaximumNumberOfThreadsCustomerOrder));
                }
                break;

            case fieldNum(RetailParameters, AllowExchangeOnReturnOrders):
                if (!this.AllowExchangeOnReturnOrders && RetailUnifiedReturnsFeatureExposure::isEnabled())
                {
                    ret = checkFailed("@RetailReturn:CannotDisableExchangeOnReturnOrdersWhileUnifiedReturnsEnabled");
                }
                break;
            case fieldNum(RetailParameters, GiftcardItem):
                ret = this.validateGiftcardItemIsBlocked();
				if (ret)
				{
                	ret = this.validateGiftcardItemWithUnitOfMeasure();
				}
                break;
            case fieldNum(RetailParameters, PickUpDeliveryModeCode):
                ret = this.validatePickUpDeliveryModeCode();
                break;
            case fieldNum(RetailParameters, CarryOutDeliveryModeCode):
                ret = this.validateCarryOutDeliveryModeCode();
                break;
            case fieldNum(RetailParameters, ElectronicDeliveryModeCode):
                ret = this.validateElectronicDeliveryModeCode();
                break;
        }

        return ret;
    }

/// <summary>
/// Indicates whether the sales statistics entries are changed.
/// </summary>
/// <returns>
/// true if the sales statistics on is changed; otherwise, false.
/// </returns>
boolean validateItemSalesStatisticsOn()
{
    if (this.ItemSalesStatisticsOn != this.orig().ItemSalesStatisticsOn && this.ItemSalesStatisticsOn != RetailNoneItemGroupDepartment::None)
    {
        if (Box::yesNo(strFmt("@RET4026",this.ItemSalesStatisticsOn),
                       DialogButton::Yes, "@RET4027") == DialogButton::No)
        {
            return false;
        }
    }

    return true;
}

/// <summary>
/// checks for item configuration 'Block at Register' is set for giftcardItem.
/// </summary>
/// <returns>
/// false if 'Block at Register' is set as No, otherwise true.
/// </returns>
boolean validateGiftcardItemIsBlocked()
{
    boolean ret = true;
    RetailInventTable retailInventTable;
    select retailInventTable where retailInventTable.itemId == this.GiftcardItem;

    // If item configuration for 'Block at Register' is set as No then it is not allowed as giftcard item.
    if (!retailInventTable.blockedOnPOS)
    {
        ret = checkFailed("@Retail:GiftcardItemIsNotBlocked");
    }
    return ret;
}

/// <summary>
/// Checks for item configuration 'Sales Unit of Measure' is set for gift card item.
/// </summary>
/// <returns>
/// false if item has no sales unit of measure, otherwise true.
/// </returns>
private boolean validateGiftcardItemWithUnitOfMeasure()
{
    boolean ret = true;
    InventTableModule inventTableModule = InventTableModule::find(this.GiftcardItem, ModuleInventPurchSales::Sales);

    if (!inventTableModule.UnitId)
    {
        ret = checkFailed("@RetailPricing:GiftcardItemHasNoSalesUnit");
    }
    return ret;
}

/// <summary>
/// Validate password strength based on password type.
/// </summary>
/// <param name="_password">
/// The password.
/// </param>
/// <param name="passwordType">
/// Password type.
/// </param>
/// <returns>
/// Returns true if password strength is valid, otherwise false.
/// </returns>
public boolean validatePasswordStrengthbyType(str _password, int passwordType)
{
    System.Text.RegularExpressions.Match regExMatch;
    boolean isValid = false;

    switch (passwordType)
    {
    case 0: // Numeric Type
        regExMatch = System.Text.RegularExpressions.Regex::Match(_password,@'(?=.*\d)');
        isValid = regExMatch.get_Success();
        break;
    case 1: // UpperCase Type
        regExMatch = System.Text.RegularExpressions.Regex::Match(_password,@'(?=.*[A-Z])');
        isValid = regExMatch.get_Success();
        break;
    case 2:  // Special Characters Type
        container charsSpecial = [
            '~','`','!','@','#','$','%','^','&','*','(',')','-','_','=','+','{','}','[',']',';',':',',','.','<','>','/','?'
        ];

        for (int i = 0; i <= conLen(charsSpecial); i++) {
            // If any special characters are found, we mark the password strength as valid.
            if (strContains(_password, conPeek(charsSpecial, i + 1))) {
                isValid = true;
                break;
            }
        }
        break;
    }

    return isValid;
}

/// <summary>
/// Sets the settlement type based on the transaction type.
/// </summary>
/// <param name="_transactionType">
/// The type of transaction.
/// </param>
/// <returns>
/// The settlement type of transaction.
/// </returns>
static SettlementType autoSettle(RetailTransactionType _transactionType)
{
    SettlementType  settlementType;
;
    switch (_transactionType)
    {
        case    RetailTransactionType::Payment :

            settlementType = RetailParameters::find().AutoSettle ? CustParameters::find().AutoSettle ? SettlementType::OpenTransact : SettlementType::None : SettlementType::None;

        break;
        default :

            settlementType = RetailParameters::find().AutoSettle ? SettlementType::SelectedTransact : SettlementType::None;
    }

    return settlementType;
}

/// <summary>
/// Finds the specified record in the <c>RetailParameters</c> table.
/// </summary>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>RetailParameters</c> table; otherwise, an empty record.
/// </returns>
static RetailParameters find(boolean _forUpdate = false)
{
    RetailParameters parameter;

    parameter.selectForUpdate(_forUpdate);

    select firstonly parameter
        index keyIdx
        where parameter.key == 0;

    if (!parameter && !parameter.isTmp())
    {
        if (RetailTaxInitTaxCalcBehaviorValueInRetailParameterCreateFlight::instance().isEnabled())
        {
            parameter.CustomerOrderTaxCalculationBehavior = RetailCustomerOrderTaxCalculationBehavior::DontRecalculate;
        }

        Company::createParameter(parameter);
    }

    return parameter;
}

/// <summary>
/// Finds the specified record in the <c>RetailParameters</c> table.
/// </summary>
/// <param name="_conCompanies">
/// A list of companies to search the retail parameter.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>RetailParameters</c> table; otherwise, an empty record.
/// </returns>
static RetailParameters findCrossCompany(container _conCompanies, boolean _forUpdate = false)
{
    RetailParameters parameter;
    container conCompanies = _conCompanies;

    parameter.selectForUpdate(_forUpdate);

    select firstonly crossCompany :conCompanies parameter
        index keyIdx
        where parameter.Key == 0;

    return parameter;
}

