private boolean checkHasSameValueInAllGiftCardCompanies(FieldId _fieldIdToCheck)
{
    boolean          ret = true;
    RetailParameters retailParameters;

    if (this.GiftCardCompany == this.dataAreaId)
    {
        // For gift card company checks retail parameter in all companies linked to current company
        while select crosscompany dataAreaId from retailParameters
            where retailParameters.RecId             != this.RecId      &&
                  retailParameters.GiftCardCompany   == this.dataAreaId &&
                  retailParameters.(_fieldIdToCheck) != this.(_fieldIdToCheck)
        {
            ret = checkFailed(strFmt("@SYP4880790", fieldId2pname(tableNum(RetailParameters), _fieldIdToCheck), this.dataAreaId, retailParameters.dataAreaId));
        }
    }
    else
    {
        // For non-gift card company checks retail parameter in related gift card company
        select firstOnly crosscompany RecId from retailParameters
            where retailParameters.dataAreaId        == this.GiftCardCompany &&
                  retailParameters.(_fieldIdToCheck) != this.(_fieldIdToCheck);

        if (retailParameters)
        {
            ret = checkFailed(strFmt("@SYP4880790", fieldId2pname(tableNum(RetailParameters), _fieldIdToCheck), this.GiftCardCompany, this.dataAreaId));
        }
    }

    return ret;
}

