public void write()
{
    if (this.orig().GiftcardItem != this.GiftcardItem)
    {
        // Update the gift card item id's for all internal gift cards in the <c>RetailStoreTenderTypeTable</c> table.
        // This action is performed with the constraints of a legal entity.
        RetailStoreTenderTypeTable::updateAllInternalGiftCardItemIds(this.GiftcardItem);
    }

    super();
}

