/// <summary>
/// Checks if sales and returns must be aggregated.
/// </summary>
/// <returns>True if aggregation is required; otherwise, false.</returns>
public static boolean isAggregateSalesAndReturns_W()
{
    boolean ret;
    #EECountryRegionCodes

    if (SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAllandRU))
    {
        RetailParameters prm = RetailParameters::find();

        ret = prm.aggregateBeforePosting && !prm.SkipAggregationForReturns && prm.AggregateSalesReturns_RU;
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret && !this.aggregateBeforePosting)
    {
        if (this.SkipAggregationForReturns)
        {
            ret = checkFailed(strFmt("@Retail:OptionCanBeSelectedOnlyIfAnotherIsSelected",
                fieldId2pname(tableNum(RetailParameters), fieldNum(RetailParameters, SkipAggregationForReturns)),
                fieldId2pname(tableNum(RetailParameters), fieldNum(RetailParameters, aggregateBeforePosting))));
        }

        if (this.AggregateSalesReturns_RU)
        {
            ret = checkFailed(strFmt("@Retail:OptionCanBeSelectedOnlyIfAnotherIsSelected",
                fieldId2pname(tableNum(RetailParameters), fieldNum(RetailParameters, AggregateSalesReturns_RU)),
                fieldId2pname(tableNum(RetailParameters), fieldNum(RetailParameters, aggregateBeforePosting))));
        }
    }

    if (ret && this.aggregateBeforePosting)
    {
        if (this.SkipAggregationForReturns && this.AggregateSalesReturns_RU)
        {
            ret = checkFailed(strFmt("@Retail:OptionCanBeSelectedOnlyIfAnotherIsSelectedAndAnotherIsNotSelect",
                fieldId2pname(tableNum(RetailParameters), fieldNum(RetailParameters, AggregateSalesReturns_RU)),
                fieldId2pname(tableNum(RetailParameters), fieldNum(RetailParameters, aggregateBeforePosting)),
                fieldId2pname(tableNum(RetailParameters), fieldNum(RetailParameters, SkipAggregationForReturns))));
        }
    }

    if ((this.CustomerOrderTaxCalculationBehavior != RetailCustomerOrderTaxCalculationBehavior::DontRecalculate) &&
        FeatureStateProvider::isFeatureEnabled(RetailReturnsTaxCalculationEnableFeature::instance()))
    {
        return checkFailed("@RetailTax:CannotChangeTaxCalculationBehavior");
    }

    if (this.isGiftCardCompanyEmpty())
    {
        ret = checkFailed("@Retail:GiftCardCompanyMustBeFilled");
    }

    if (!this.validateCancellationChargeCode())
    {
        ret = false;
    }

    return ret;
}

/// <summary>
/// Validates PickUpDeliveryModeCode value.
/// </summary>
/// <returns>
/// true if there are no open sales orders with PickUpDeliveryModeCode entered; otherwise, false.
/// </returns>
private boolean validatePickUpDeliveryModeCode()
{
    if (this.PickUpDeliveryModeCode != this.orig().PickUpDeliveryModeCode)
    {
        SalesTable salesTable;

        select firstonly salesTable
            where salesTable.DlvMode == this.PickUpDeliveryModeCode
            && salesTable.salesStatus == SalesStatus::Backorder;

        if (salesTable)
        {
            checkFailed(strFmt('@Retail:InvalidPickUpDeliveryModeCode', this.PickUpDeliveryModeCode));
            return false;
        }
    }

    if ((this.CarryOutDeliveryModeCode && this.PickUpDeliveryModeCode)
        && (this.CarryOutDeliveryModeCode == this.PickUpDeliveryModeCode))
    {
        checkFailed("@Retail:PickupCarryOutDlvModeValidation");
        return false;
    }
    else if ((this.ElectronicDeliveryModeCode && this.PickUpDeliveryModeCode)
            && (this.ElectronicDeliveryModeCode == this.PickUpDeliveryModeCode))
    {
        checkFailed("@Retail:PickupElectronicDlvModeValidation");
        return false;
    }

    return true;
}

/// <summary>
/// Validates CarryOutDeliveryModeCode value.
/// </summary>
/// <returns>
/// True if there are no same mode of delivery matching with CarryOutDeliveryModeCode entered; otherwise, false.
/// </returns>
private boolean validateCarryOutDeliveryModeCode()
{
    if ((this.CarryOutDeliveryModeCode && this.PickUpDeliveryModeCode)
        && (this.CarryOutDeliveryModeCode == this.PickUpDeliveryModeCode))
    {
        checkFailed("@Retail:PickupCarryOutDlvModeValidation");
        return false;
    }
    else if ((this.CarryOutDeliveryModeCode && this.ElectronicDeliveryModeCode)
            && (this.CarryOutDeliveryModeCode == this.ElectronicDeliveryModeCode))
    {
        checkFailed("@Retail:CarryOutElectronicDlvModeValidation");
        return false;
    }

    return true;
}

/// <summary>
/// Validates ElectronicDeliveryModeCode value.
/// </summary>
/// <returns>
/// True if there are no same mode of delivery matching with ElectronicDeliveryModeCode entered; otherwise, false.
/// </returns>
private boolean validateElectronicDeliveryModeCode()
{
    if ((this.CarryOutDeliveryModeCode && this.ElectronicDeliveryModeCode)
        && (this.CarryOutDeliveryModeCode == this.ElectronicDeliveryModeCode))
    {
        checkFailed("@Retail:CarryOutElectronicDlvModeValidation");
        return false;
    }
    else if ((this.ElectronicDeliveryModeCode && this.PickUpDeliveryModeCode)
            && (this.ElectronicDeliveryModeCode == this.PickUpDeliveryModeCode))
    {
        checkFailed("@Retail:PickupElectronicDlvModeValidation");
        return false;
    }

    return true;
}

public boolean isGiftCardCompanyEmpty()
{
    return (this.GiftCardCompany == '');
}

/// <summary>
/// Verifies that the value of the <c>CancellationChargeCode</c> field is set correctly.
/// </summary>
/// <returns>
/// True if there is no same charge code being used in advanced auto charges; otherwise, false.
/// </returns>
private boolean validateCancellationChargeCode()
{
    MarkupAutoTable markupAutoTable;
    MarkupAutoLine markupAutoLine;

    // Avoid Cust type charge code which has been used in advanced auto charges
    if (this.UseAdvancedAutoCharges)
    {
        select firstonly RecId from markupAutoLine
            where markupAutoLine.ModuleType == MarkupModuleType::Cust
            && markupAutoLine.MarkupCode == this.CancellationChargeCode
            && markupAutoLine.DataAreaId == this.DataAreaId;

        if (markupAutoLine)
        {
            checkFailed(strFmt("@RetailCharge:CancellationChargeCodeHasBeenUsedWarning", this.CancellationChargeCode));
            return false;
        }
    }
    else
    {
        if (this.ShippingChargeCode != "" && this.ShippingChargeCode == this.CancellationChargeCode)
        {
            checkFailed(strFmt("@RetailCharge:CancellationChargeCodeDuplicatedWithShippingChargeCodeWarning", this.CancellationChargeCode));
            return false;
        }
    }
    return true;

}
