/// <summary>
/// Validates the <c>allowChangeNoVoid</c> field and indicates whether the
/// <c>allowTransactionVoiding</c> field has been checked.
/// </summary>
/// <returns>
/// false if the <c>allowTransactionVoiding</c> field is checked or if it is null; otherwise, true.
/// </returns>
boolean validateAllowChangeNoVoid()
{
    boolean ret;

    if (this.AllowTransactionVoiding && this.AllowChangeNoVoid)
    {
        ret = checkFailed(strFmt("@RET4032", fieldId2pname(tableNum(RetailPosPermissionGroup), fieldNum(RetailPosPermissionGroup, AllowTransactionVoiding))));
    }
    else
    {
        ret = true;
    }

    return ret;
}

/// <summary>
/// Validates the <c>allowPriceOverride</c> field.
/// </summary>
/// <returns>
/// True if the value selected is allowed in Brazil; otherwise, false.
/// </returns>
boolean validateAllowPriceOverride_BR()
{
    boolean ret = true;

    switch (this.AllowPriceOverride)
    {
        case RetailPriceOverride::HigherAndLower:
        case RetailPriceOverride::HigherOnly:
            ret = checkFailed("@SYS4007456");
            break;
    }

    return ret;
}

/// <summary>
/// Sets the <c>allowChangeNoVoid</c> field to false.
/// </summary>
void validateAllowTransactionVoiding()
{
    if (this.AllowTransactionVoiding)
    {
        this.AllowChangeNoVoid = false;
    }
}

public boolean validateDelete()
{
    boolean ret;
    RetailPositionPosPermission retailPositionPosPermission;

    ret = super();

    select retailPositionPosPermission
        where retailPositionPosPermission.PosPermissionGroup == this.RecId;

    if (retailPositionPosPermission)
    {
        ret = checkFailed("@RET5814"); // "Cannot delete the record"
    }

    return ret;
}

/// <summary>
/// Validates the discount percentage value.
/// </summary>
/// <param name="_disccountPct">
/// The percentage of the discount.
/// </param>
/// <returns>
/// true if discount percentage value is in between 0 and 100; otherwise, false.
/// </returns>
boolean validateDiscountPct(RetailMaxDiscountPct _disccountPct)
{
    boolean ret = true;

    if (_disccountPct > 100)
    {
        ret = checkFailed("@RET4033");
    }
    else if (_disccountPct < 0)
    {
        ret = checkFailed("@RET4034");
    }

    return ret;
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;

    ret = super(_fieldIdToCheck);

    switch (fieldExt2Id(_fieldIdToCheck))
    {
        case fieldNum(RetailPosPermissionGroup, AllowChangeNoVoid):
            ret = this.validateAllowChangeNoVoid();
            break;
        case fieldNum(RetailPosPermissionGroup, AllowTransactionVoiding):
            this.validateAllowTransactionVoiding();
            break;
        case fieldNum(RetailPosPermissionGroup, MaximumDiscountPct):
            ret = this.validateDiscountPct(this.MaximumDiscountPct);
            break;
        case fieldNum(RetailPosPermissionGroup, MaxTotalDiscountPct):
            ret = this.validateDiscountPct(this.MaxTotalDiscountPct);
            break;
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        ret = this.validateField_BR(_fieldIdToCheck) && ret;
    }
    // </GBR>

    return ret;
}

/// <summary>
/// Determines whether the <paramref name="_fieldIdToCheck"/> field is valid.
/// </summary>
/// <param name="_fieldIdToCheck">
/// The field ID of the field to validate.
/// </param>
/// <returns>
/// true if the field is valid; otherwise, false.
/// </returns>
/// <remarks>
/// Extension method for Brazilian fields.
/// </remarks>
boolean validateField_BR(FieldId _fieldIdToCheck)
{
    boolean ret = true;

    switch (fieldExt2Id(_fieldIdToCheck))
    {
        case fieldNum(RetailPosPermissionGroup, allowPriceOverride):
            ret = this.validateAllowPriceOverride_BR();
            break;
    }

    return ret;
}
