/// <summary>
/// Indicates whether the <c>allowChangeNoVoid</c> field of the <c>RetailPosPermissionGroup</c> table
/// is valid.
/// </summary>
/// <returns>
/// true if the field is valid; otherwise, false.
/// </returns>
boolean validateAllowChangeNoVoid()
{
    boolean ret;

    if (this.AllowTransactionVoiding && this.AllowChangeNoVoid)
    {
        ret = checkFailed(strFmt("@RET4032", fieldId2pname(tableNum(RetailPosPermissionGroup), fieldNum(RetailPosPermissionGroup, AllowTransactionVoiding))));
    }
    else
    {
        ret = true;
    }

    return ret;
}

/// <summary>
/// Validates the <c>allowPriceOverride</c> field.
/// </summary>
/// <returns>
/// True if the value selected is allowed in Brazil; otherwise, false.
/// </returns>
boolean validateAllowPriceOverride_BR()
{
    boolean ret = true;

    switch (this.AllowPriceOverride)
    {
        case RetailPriceOverride::HigherAndLower:
        case RetailPriceOverride::HigherOnly:
            ret = checkFailed("@SYS4007456");
            break;
    }

    return ret;
}

/// <summary>
/// Indicates whether the <c>AllowTransactionVoiding</c> field of the <c>RetailPosPermissionGroup</c>
/// table is valid.
/// </summary>
void validateAllowTransactionVoiding()
{
    if (this.AllowTransactionVoiding)
    {
        this.AllowChangeNoVoid = false;
    }
}

