/// <summary>
/// Validates the <c>AllowTransactionVoiding</c> field of the <c>RetailPosPermissionGroup</c> table.
/// </summary>
/// <param name="_disccountPct">
/// The discount percent field value of the <c>RetailPosPermissionGroup</c> table.
/// </param>
/// <returns>
/// true if the discount percent is between zero and one hundred; otherwise, false.
/// </returns>
boolean validateDiscountPct(RetailMaxDiscountPct _disccountPct)
{
    boolean ret = true;

    if (_disccountPct > 100)
    {
        ret = checkFailed("@RET4033");
    }
    else if (_disccountPct < 0)
    {
        ret = checkFailed("@RET4034");
    }

    return ret;
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;

    ret = super(_fieldIdToCheck);

    switch (fieldExt2Id(_fieldIdToCheck))
    {
        case fieldNum(RetailPositionPosPermission, AllowChangeNoVoid):
            ret = this.validateAllowChangeNoVoid();
            break;
        case fieldNum(RetailPositionPosPermission, AllowTransactionVoiding):
            this.validateAllowTransactionVoiding();
            break;
        case fieldNum(RetailPositionPosPermission, MaximumDiscountPct):
            ret = this.validateDiscountPct(this.MaximumDiscountPct);
            break;
        case fieldNum(RetailPositionPosPermission, MaxTotalDiscountPct):
            ret = this.validateDiscountPct(this.MaxTotalDiscountPct);
            break;
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        ret = this.validateField_BR(_fieldIdToCheck) && ret;
    }
    // </GBR>

    return ret;
}

/// <summary>
/// Determines whether the <paramref name="_fieldIdToCheck"/> field is valid.
/// </summary>
/// <param name="_fieldIdToCheck">
/// The field ID of the field to validate.
/// </param>
/// <returns>
/// true if the field is valid; otherwise, false.
/// </returns>
/// <remarks>
/// Extension method for Brazilian fields.
/// </remarks>
boolean validateField_BR(FieldId _fieldIdToCheck)
{
    boolean ret = true;

    switch (fieldExt2Id(_fieldIdToCheck))
    {
        case fieldNum(RetailPositionPosPermission, allowPriceOverride):
            ret = this.validateAllowPriceOverride_BR();
