    }

    return ret;
}

/// <summary>
/// Finds the specified record in the <c>RetailPositionPosPermission</c> table.
/// </summary>
/// <param name="_position">
/// The record ID of the HCM Position record.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>RetailPositionPosPermission</c> table; otherwise, an empty record.
/// </returns>
static public RetailPositionPosPermission find(HcmPositionRecId _position, boolean _forUpdate = false)
{
    RetailPositionPosPermission positionPosPermission;

    select firstOnly positionPosPermission
        where positionPosPermission.Position == _position;

    positionPosPermission.selectForUpdate(_forUpdate);

    return positionPosPermission;
}
