/// <summary>
/// Check for settings in the fields of table <c>RetailProductAttributesToUpdate</c>.
/// </summary>
/// <returns>
/// A boolean value to indicate whether the settings for the fields are done; otherwise false.
/// </returns>
public boolean isAnythingSet()
{
    SysDictTable attributesToUpdate;
    int fields;
    int fieldIndex;
    SysDictField fromDictField;
    boolean settingsPresent = false;

    attributesToUpdate = new SysDictTable(tableNum(RetailProductAttributesToUpdate));

    fields = attributesToUpdate.fieldCnt();

    for (fieldIndex = 1; fieldIndex <= fields; fieldIndex++)
    {
        fromDictField = new SysDictField(attributesToUpdate.id(),attributesToUpdate.fieldCnt2Id(fieldIndex));

        if (!fromDictField.isSystem() && (fromDictField.typeName() == enumStr(NoYes)))
        {
            if (this.(fromDictField.id()))
            {
                settingsPresent = true;
                break;
            }
        }
    }

    return settingsPresent;
}

/// <summary>
/// Sets all fields to the <c>_newValue</c> value.
/// </summary>
/// <param name="_newValue">
/// The new value to be set.
/// </param>
public void toggleAll(NoYes _newValue)
{
    SysDictTable productAttributesGlobal;
    SysDictTable productLegalEntityAttributes;
    SysDictField fromDictField;

    int fields;
    int fieldIndex;
    int toFieldId;
    str fromFieldName;

    productAttributesGlobal = new SysDictTable(tableNum(RetailAttributesGlobalLookup));
    productLegalEntityAttributes = new SysDictTable(tableNum(RetailAttributesLegalEntityLookup));

    fields = productAttributesGlobal.fieldCnt();

    for (fieldIndex = 1; fieldIndex <= fields; fieldIndex++)
    {
        fromDictField = new SysDictField(productAttributesGlobal.id(),productAttributesGlobal.fieldCnt2Id(fieldIndex));

        if (!fromDictField.isSystem())
        {
            fromFieldName = fromDictField.name();
            // Exclude selection of retail variant groups
            if ((fromFieldName != fieldStr(RetailProductAttributesToUpdate, VariantColorGroup))
                && (fromFieldName != fieldStr(RetailProductAttributesToUpdate, VariantSizeGroup))
                && (fromFieldName != fieldStr(RetailProductAttributesToUpdate, VariantStyleGroup)))
            {
                toFieldId = fieldName2id(this.TableId, fromFieldName);
                if (toFieldId != 0)
                {
                    this.(toFieldId) = _newValue;
                }
            }
        }
    }

    fields = productLegalEntityAttributes.fieldCnt();

    for (fieldIndex = 1; fieldIndex <= fields; fieldIndex++)
    {
        fromDictField = new SysDictField(productLegalEntityAttributes.id(), productLegalEntityAttributes.fieldCnt2Id(fieldIndex));

        if (!fromDictField.isSystem())
        {
            fromFieldName = fromDictField.name();
            toFieldId = fieldName2id(this.TableId, fromFieldName);
            if (toFieldId != 0)
            {
                this.(toFieldId) = _newValue;
            }
        }
    }
}

