public static void deleteByEntityTypeAndId(RetailProductUpdateMode _mode, RefRecId _entityId)
{
    RetailProductAttributesToUpdate retailProductAttributesToUpdate;

    delete_from retailProductAttributesToUpdate
    where retailProductAttributesToUpdate.EntityType == _mode &&
          retailProductAttributesToUpdate.EntityId == _entityId;
}

