public static RetailProductAttributesToUpdate find(RecId _recId, boolean _forUpdate = false)
{
    RetailProductAttributesToUpdate retailProductAttributesToUpdate;

    retailProductAttributesToUpdate.selectForUpdate(_forUpdate);

    select firstonly retailProductAttributesToUpdate where retailProductAttributesToUpdate.RecId == _recId;
    return retailProductAttributesToUpdate;
}

