/// <summary>
/// Validates that if all discounts are not allowed, manual discount is not allowed.
/// </summary>
boolean validatePOSDisallowDiscount()
{
    this.RetailPOSDisallowManualDiscount = this.RetailPOSDisallowDiscount;

    return true;
}

/// <summary>
/// Validates fields.
/// </summary>
/// <param name = "_fieldIdToCheck">The field to check.</param>
public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;

    ret = super(_fieldIdToCheck);

    switch (fieldExt2Id(_fieldIdToCheck))
    {
        case fieldNum(RetailProductAttributesToUpdate, RetailPOSDisallowDiscount):
            ret = this.validatePOSDisallowDiscount();
            break;
    }

    return ret;
}
