private void setDefaultIfEmpty()
{
    str aesEncryption = 'AES';
    str shaHash = 'SHA256';

    if (!this.DeviceTokenExpiration || this.DeviceTokenExpiration == 0)
    {
        // default expiration is 10 years (3650 days)
        const int DefaultDeviceTokenExpiration = 10 * 365;
        this.DeviceTokenExpiration = DefaultDeviceTokenExpiration;
    }

    if (!this.EFTPasswordEncryption)
    {
        this.EFTPasswordEncryption = aesEncryption;
    }

    if (!this.TSPasswordEncryption)
    {
        this.TSPasswordEncryption = shaHash;
    }

    if (!this.StaffPasswordHash)
    {
        this.StaffPasswordHash = shaHash;
    }

    if (!this.DeviceTokenAlgorithm)
    {
        this.DeviceTokenAlgorithm = shaHash;
    }

    if (!this.ClxStorageSasKeyLifetimeInHours || this.ClxStorageSasKeyLifetimeInHours == 0)
    {
        this.ClxStorageSasKeyLifetimeInHours = RetailMediaHelper::DefaultClxSasKeyLifetimeInHours;
    }

    if (!this.ClxStorageSasKeyRecycleWindowInHours || this.ClxStorageSasKeyRecycleWindowInHours == 0)
    {
        this.ClxStorageSasKeyRecycleWindowInHours = RetailMediaHelper::DefaultClxSasKeyRecycleWindowInHours;
    }
}

/// <summary>
/// Updates the record in <c>RetailSharedParameters</c> table.
/// </summary>
/// <remarks>
/// If replication of the record is allowed for the table, action is inserted into the <c>RetailConnPreactionTable</c> table.
/// </remarks>
public void update()
{
    this.setDefaultIfEmpty();

    boolean inventoryServiceAsDataSourceTurnOn = this.UseInventoryServiceAsDataSource == NoYes::Yes && this.orig().UseInventoryServiceAsDataSource == NoYes::No;
    if (inventoryServiceAsDataSourceTurnOn)
    {
        this.setInventoryServiceIntegrationResetDateTimeToUtcNow();
    }

    super();
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;
    boolean isValidUrl;
    // max expiration is 10 years (3650 days)
    const int MaxDeviceTokenExpiration = 10 * 365;

    System.Uri uri;
    RetailSelfServicePackageInfo pkgInfoTable;
    ret = super(_fieldIdToCheck);

    switch (_fieldIdToCheck)
    {
        case (fieldNum(RetailSharedParameters, SharePointAssetPickerUrl)):
            ret = this.validateSharePointAssetPickerUrl();
            break;

        case (fieldNum(RetailSharedParameters, DeploymentRootPath)):
            if (this.DeploymentRootPath)
            {
                isValidUrl = System.Uri::TryCreate(this.DeploymentRootPath, System.UriKind::Absolute, byRef uri);

                if (!isValidUrl || !uri.get_IsUnc())
                {
                    // Deployment root path is not in UNC format.
                    ret = checkFailed("@SYP4910205");
                }
            }
            break;

        case fieldNum(RetailSharedParameters, DeviceTokenExpiration) :
            if (this.DeviceTokenExpiration > MaxDeviceTokenExpiration) //check for not more than 3650 (10 * 365) days
            {
                ret = Global::checkFailed(strFmt("@Retail:DeviceTokenExpiration", MaxDeviceTokenExpiration));
            }
            break;

        case (fieldNum(RetailSharedParameters, UseInventoryServiceAsDataSource)):
            ret = RetailInventoryServiceIntegrationHelper::validateUseInventoryServiceAsDataSource(this);
            break;
    }

    return ret;
}

/// <summary>
/// Calls the method when a field name is validated.
/// </summary>
/// <param name="_fieldName">
/// The value of the <c>FieldName</c>
/// </param>
/// <param name="_arrayIndex">
/// The integer value of arrayIndex.
/// </param>
/// <returns>
/// Returns true if the field name is validate against name and re-encripted
/// </returns>
public boolean validateFieldValue(FieldName _fieldName, int _arrayIndex = 1)
{
    boolean ret;
    str algorithmNameBeforeUpdate;
    str data;
    RetailHardwareProfile retailHardwareProfile;
    str fallbackAlgorithmName = 'AES';

    ret = super(_fieldName, _arrayIndex);

    if (ret)
    {
        switch (_fieldName)
        {
            case fieldStr(RetailSharedParameters, StaffPasswordHash):
                // Validate name, re-hash not possible.
                ret = RetailData::IsValidHashName(this.StaffPasswordHash);

                if (!ret)
                {
                    checkFailed("@RET261036"); // Invalid algorithm name.
                }
                break;
            case fieldStr(RetailSharedParameters, EnableOmniChannelPayments) :
                if (this.orig().EnableOmniChannelPayments == NoYes::Yes && this.EnableOmniChannelPayments == NoYes::No && RetailCommercePaymentsFeatureExposure::isEnabled())
                {
                    ret = Global::checkFailed(strFmt("@Retail:RetailFeatureIsEnabledSoCannotDisableParameter", "@Retail:RetailEnableOmniChannelPayments", "@Retail:RetailCommercePaymentFeatureKey"));
                }
                break;
        }
    }

    return ret;
}

private boolean validateSharePointAssetPickerUrl()
{
    #define.Http('http')
    #define.Https('https')

    System.Uri url;
    boolean isValidUrl;
    str scheme;

    if (this.SharepointAssetPickerUrl)
    {
        isValidUrl = System.Uri::TryCreate(this.SharepointAssetPickerUrl, System.UriKind::Absolute, byRef url);
        if (!isValidUrl)
        {
            return checkFailed("@RET490165");
        }
        else if (!url.get_IsFile())
        {
            scheme = url.get_Scheme();
            if (scheme != #Https && scheme != #Http)
            {
                return checkFailed("@RET490165");
            }
        }
    }

    return true;
}

/// <summary>
/// Finds or creates a record in the <c>RetailSharedParameters</c> table.
/// </summary>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>RetailSharedParameters</c> table; otherwise, an empty record.
/// </returns>
static RetailSharedParameters find(boolean _forUpdate = false)
{
    RetailSharedParameters parameter;

    parameter.selectForUpdate(_forUpdate);

    select firstonly parameter
        where parameter.Key == 0;

    if (!parameter && !parameter.isTmp())
    {
        Company::createParameter(parameter);
    }

    return parameter;
}

/// <summary>
/// Finds a record in <c>RetailSharedParameters</c> table.
/// </summary>
/// <param name="_key">
/// The key value for the <c>RetailSharedParameters</c>.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in table <c>RetailSharedParameters</c>.
/// </returns>
static RetailSharedParameters findByKey(ParametersKey _key, boolean _forUpdate = false)
{
    RetailSharedParameters parameter;

    parameter.selectForUpdate(_forUpdate);

    select firstonly parameter
        where parameter.Key == _key;

    return parameter;
}

/// <summary>
/// Returns the lifetime of the Clx Storage aka Blob Container SAS key.
/// </summary>
/// <returns>
/// Sas key lifetime in hours.
/// </returns>
public static int ClxSasKeyLifetimeInHours()
{
    int result = RetailSharedParameters::find().ClxStorageSasKeyLifetimeInHours;
    if (result == 0)
    {
        result = RetailMediaHelper::DefaultClxSasKeyLifetimeInHours;
    }

    return result;
}

/// <summary>
/// Returns the Clx Storage aka Blob Container SAS key recycle window.
/// </summary>
/// <returns>
/// Sas key recycle window in hours.
/// </returns>
public static int ClxSasKeyRecycleWindowInHours()
{
    int result = RetailSharedParameters::find().ClxStorageSasKeyRecycleWindowInHours;
    if (result == 0)
    {
        result = RetailMediaHelper::DefaultClxSasKeyRecycleWindowInHours;
    }

    return result;
}

/// <summary>
/// Returns the AX2012 registry key Data2 value which is part of the RetailSalt data.  This value is used when AX must hash for legacy AX2012 channels. 
/// </summary>
/// <returns>Salt data2.</returns>
public static str TryGetLegacyRegKeyData2Value()
{
    RetailSharedParameters retailSharedParameters;
    str result;

    if (isConfigurationkeyEnabled(configurationKeyNum(RetailCDXBackwardCompatibility)))
    {
        retailSharedParameters = RetailSharedParameters::find();
        result = retailSharedParameters.LegacyRegKeyData2Value;
    }
    else
    {
        result = '';
    }

    return result;
}

/// <summary>
/// Returns the validation template id.
/// </summary>
/// <returns>Template id.</returns>
public static DMFDefinitionGroupName TryGetEntityValidationTemplate()
{
    RetailSharedParameters retailSharedParameters = RetailSharedParameters::find();
    return retailSharedParameters.EntityValidationTemplate;
}

/// <summary>
/// Returns the validation template id. Throws an error if template id is null.
/// </summary>
/// <returns>Template id.</returns>
public static DMFDefinitionGroupName getEntityValidationTemplate()
{
    DMFDefinitionGroupName templateId = RetailSharedParameters::TryGetEntityValidationTemplate();

    if (templateId)
    {
        return templateId;
    }
    else
    {
        throw Global::error("@Retail:EntityValidationTemplateIsNotDefined");
    }
}

