internal static void updateInventoryServiceIntegrationResetDateTime()
{
    ttsbegin;
    RetailSharedParameters parameters = RetailSharedParameters::find(true);
    parameters.setInventoryServiceIntegrationResetDateTimeToUtcNow();
    parameters.update();
    ttscommit;
}
