    public static void InsertToRegularTable(RetailTransactionDiscountTransX cursor)
{
    RetailTransactionDiscountTrans target;
    RetailTransactionDiscountTransX cursor2;
    int maxRetry = 5;
    int retryCount = 0;
    int i = 0;
    container companies;
    DataAreaID le;

    while select crossCompany DataAreaId from cursor group by DataAreaId
    {
        companies += cursor.dataAreaId;
    }

    try
    {
        ttsBegin;
        ++retryCount;

        for (i = 1; i <= conLen(companies); ++i)
        {
            le = conPeek(companies, i);

            changecompany(le)
            {
                target = null;
                cursor2 = null;
                cursor2.linkPhysicalTableInstance(cursor);

                delete_from cursor2
                index hint pkidx
                exists join target
                index hint TransactionDiscountTransIdx
                where (target.@StoreId==cursor2.@StoreId && target.@TerminalId==cursor2.@TerminalId && target.@TransactionId==cursor2.@TransactionId && target.@SaleLineNum==cursor2.@SaleLineNum && target.@PeriodicDiscountOfferId==cursor2.@PeriodicDiscountOfferId && target.@Channel==cursor2.@Channel);

                cursor2.clear();
                insert_recordset target(
                    @Amount,
                    @Channel,
                    @DiscountCode,
                    @PeriodicDiscountOfferId,
                    @ReplicationCounterFromOrigin,
                    @SaleLineNum,
                    @StoreId,
                    @TerminalId,
                    @TransactionId,
                    @ORIGIN
                )
                select
                    @Amount,
                    @Channel,
                    @DiscountCode,
                    @PeriodicDiscountOfferId,
                    @ReplicationCounterFromOrigin,
                    @SaleLineNum,
                    @StoreId,
                    @TerminalId,
                    @TransactionId,
                    @ORIGIN
                    from cursor2;
            }
        }

        ttsCommit;
    }
    catch(Exception::Error)
    {
        if (retryCount >= maxRetry)
        {
            throw Exception::Error;
        }
        else
        {
            retry;
        }
    }
}
