/// <summary>
/// Initializes the <c>AmountMst</c> field of the <c>RetailTransactionPaymentTrans</c> table with the
/// calculated payment amount.
/// </summary>
void calcAmountMST()
{
    ExchRate        posExchRate = this.transactionTable().posExchRate();

    if (!this.AmountMST)
    {
        this.AmountMST = this.AmountTendered * (posExchRate / 100);
    }
    else
    {
        this.AmountMST = this.AmountMST;
    }
}

/// <summary>
/// Retrieves the amount that includes the inquiry fee and percentage fee.
/// </summary>
/// <returns>
/// The calculated amount.
/// </returns>
AmountCur calcCardFeeAmount()
{
    AmountCur   amountCur, totalCardFeeAmountCur;
    RetailStoreTenderTypeCardTable storeTenderTypeCardTable;
    int decimalPrecision = new SysDictType(extendedTypeNum(AmountCur)).effectivePrecision();

    if (this.CardTypeId)
    {
        storeTenderTypeCardTable = RetailStoreTenderTypeCardTable::find(RetailStoreTable::find(this.Store).RecId,
                                                                     this.TenderType,
                                                                     this.CardTypeId);
        if (storeTenderTypeCardTable)  // card payments are always in the currency of the store.
        {
            amountCur = this.AmountCur ? this.AmountCur : this.AmountTendered;
            // Add the inquiry fee.
            totalCardFeeAmountCur += storeTenderTypeCardTable.CardInquiryFee;
            // Add the percentage fee.
            totalCardFeeAmountCur += (storeTenderTypeCardTable.CardFee / 100) * amountCur;
            // If card fee less than min fee then set min.
            if (totalCardFeeAmountCur < storeTenderTypeCardTable.CardFeeMin && storeTenderTypeCardTable.CardFeeMin)
                totalCardFeeAmountCur = storeTenderTypeCardTable.CardFeeMin;
            // if card fee greater than max fee then set max.
            if (totalCardFeeAmountCur > storeTenderTypeCardTable.CardFeeMax && storeTenderTypeCardTable.CardFeeMax)
                totalCardFeeAmountCur = storeTenderTypeCardTable.CardFeeMax;
        }
    }

    return decRound(totalCardFeeAmountCur, decimalPrecision);
}

/// <summary>
/// Initializes the <c>ExchRateMST</c> field of the <c>RetailTransactionPaymentTrans</c> table with the
/// calculated exchange.
/// </summary>
void calcExchRateMST()
{
    ;
    if (!this.ExchRateMST && this.AmountMST)
    {
        if (this.AmountCur)
        {
            this.ExchRateMST = (this.AmountMST / this.AmountCur) * 100;
        }
        else
        {
            // Online order
            this.ExchRateMST = 100;
        }
    }
    else
    {
        this.ExchRateMST = this.ExchRateMST;
    }
}

