/// <summary>
/// Retrieves the exchange rate that is used in the payment transaction.
/// </summary>
/// <returns>
/// The exchange rate.
/// </returns>
ExchRate posPaymExchRate()
{
    ExchRate        exchRate = 0;
    ;
    if (!this.ExchRateMST)
    {
        if (!this.AmountMST)
        {
            this.calcAmountMST();
        }

        this.calcExchRateMST();

        exchRate = this.ExchRateMST;
    }
    else
    {
        exchRate = this.ExchRateMST;
    }

    return exchRate;
}

