/// <summary>
/// Finds a specified record in the <c>RetailTransactionTable</c> table.
/// </summary>
/// <returns>
/// A record in the <c>RetailTransactionTable</c> table; otherwise, an empty record.
/// </returns>
RetailTransactionTable transactionTable()
{
    return RetailTransactionTable::find(this.TransactionId,this.Store,this.Terminal);
}

/// <summary>
/// Finds a specified record in the <c>RetailTransactionPaymentTrans</c> table.
/// </summary>
/// <param name="_transactionId">
/// The transaction number of the record to find.
/// </param>
/// <param name="_storeId">
/// The store number of the record to find.
/// </param>
/// <param name="_terminalId">
/// The terminal number of the record to find.
/// </param>
/// <param name="_lineNum">
/// The line number of the record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>RetailTransactionPaymentTrans</c> table; otherwise, an empty record.
/// </returns>
public static RetailTransactionPaymentTrans find(RetailTransactionId _transactionId,RetailStoreId _storeId,RetailTerminalId _terminalId, LineNum _lineNum, boolean _forUpdate = false)
{
    RetailTransactionPaymentTrans  transactionPaymentTrans;

    if (_transactionId && _storeId && _terminalId && _lineNum)
    {
        transactionPaymentTrans.selectForUpdate(_forUpdate);

        select firstonly transactionPaymentTrans
            index hint transactionLineIdx
            where transactionPaymentTrans.Store         == _storeId
            &&    transactionPaymentTrans.Terminal      == _terminalId
            &&    transactionPaymentTrans.TransactionId == _transactionId
               && transactionPaymentTrans.LineNum == _lineNum;
    }

    return transactionPaymentTrans;
}

/// <summary>
/// Gets the RetailTenderLineStatus, which is based on the RetailEntryStatus.
/// </summary>
/// <returns>
/// The RetailTenderLineStatus.
/// </returns>
public RetailTenderLineStatus getRetailTenderLineStatus()
{
    // Convert back to TenderLineStatus from TransactionStatus
    if (this.transactionStatus == RetailEntryStatus::Voided)
    {
        return RetailTenderLineStatus::Voided;
    }
    else
    {
        return RetailTenderLineStatus::Historical;
    }
}

/// <summary>
/// Gets the payment operation for this tender line.
/// </summary>
/// <returns>
/// Returns the operation id of the payment operation used by this tender line.
/// </returns>
internal RetailOperationId getPaymentOperation()
{
    RetailStoreTenderTypeTable storeTenderTable;

    select firstonly storeTenderTable
        where storeTenderTable.Channel == this.Channel
        && storeTenderTable.tenderTypeId == this.tenderType;

    return storeTenderTable.posOperation;
}

/// <summary>
/// Displays the approval code from the payment token.
/// </summary>
/// <returns>The approval code.</returns>
internal display RetailApprovalCode getApprovalCode()
{
    RetailApprovalCode approvalCode = '';

    if (this.PaymentCaptureToken)
    {
        approvalCode = RetailTransactionPaymentTrans::getCardProperty(
            this.PaymentCaptureToken,
            PaymentSDK.GenericNamespace::get_CaptureResponse(),
            PaymentSDK.CaptureResponseProperties::get_ApprovalCode());
    }
    else if (this.paymentAuthorization)
    {
        approvalCode = RetailTransactionPaymentTrans::getCardProperty(
            this.paymentAuthorization,
            PaymentSDK.GenericNamespace::get_AuthorizationResponse(),
            PaymentSDK.AuthorizationResponseProperties::get_ApprovalCode());

        if (approvalCode == '')
        {
            // Search for the property in the refund response.
            approvalCode = RetailTransactionPaymentTrans::getCardProperty(
                this.paymentAuthorization,
                PaymentSDK.GenericNamespace::get_RefundResponse(),
                PaymentSDK.RefundResponseProperties::get_ApprovalCode());
        }
    }

    return approvalCode;
}

