/// <summary>
/// Gets the payment property value from the given token.
/// </summary>
/// <param name = "_token">The token.</param>
/// <param name = "_namespace">The namespace to search for the property.</param>
/// <param name = "_response">The property name.</param>
/// <returns>The property value.</returns>
internal static str getCardProperty(str _token, str _namespace, str _response)
{
    str propertyValue = '';

    if (EnhancedWalletSupportAndPaymentsImprovementFeature::isEnabled())
    {
        CreditCardPaymentProperty creditCardPaymentProperty = CreditCardPaymentProperties::fromXmlString(_token).find(_namespace, _response);
        propertyValue = creditCardPaymentProperty ? creditCardPaymentProperty.parmStringValue() : '';
    }

    return propertyValue;
}
