/// <summary>
/// Inserts data from the staging table to the regular table.
/// </summary>
/// <param name="cursor">
/// A record from the staging table.
/// </param>
/// <exception cref="Exception::Error">
/// Thrown if the record cannot be inserted after a number of tries.
/// </exception>
public static void InsertToRegularTable(RetailTransactionPaymentTrans_BRX cursor)
{
    RetailTransactionPaymentTrans_BR target;
    RetailTransactionPaymentTrans_BRX cursor2;
    int maxRetry = 5;
    int retryCount = 0;
    int i = 0;
    container companies;
    DataAreaID le;

    while select crossCompany DataAreaId from cursor group by DataAreaId
    {
        companies += cursor.dataAreaId;
    }

    try
    {
        ttsBegin;
        ++retryCount;

        for (i = 1; i <= conLen(companies); ++i)
        {
            le = conPeek(companies, i);

            changecompany(le)
            {
                target = null;
                cursor2 = null;
                cursor2.linkPhysicalTableInstance(cursor);

                delete_from cursor2
                index hint pkidx
                exists join target
                index hint LineNumIdx
                where (target.@lineNum==cursor2.@lineNum && target.@StoreId==cursor2.@StoreId && target.@TerminalId==cursor2.@TerminalId && target.@TransactionId==cursor2.@TransactionId);

                cursor2.clear();
                insert_recordset target(
                    @AmountCur,
                    @AmountTendered,
                    @LineNum,
                    @ReplicationCounterFromOrigin,
                    @StoreId,
                    @TenderType,
                    @TerminalId,
                    @TransactionId,
                    @TransactionStatus,
                    @TransDate,
                    @TransTime,
                    @ORIGIN
                )
                select
                    @AmountCur,
                    @AmountTendered,
                    @LineNum,
                    @ReplicationCounterFromOrigin,
                    @StoreId,
                    @TenderType,
                    @TerminalId,
                    @TransactionId,
                    @TransactionStatus,
                    @TransDate,
                    @TransTime,
                    @ORIGIN
                    from cursor2;
            }
        }

        ttsCommit;
    }
    catch(Exception::Error)
    {
        if (retryCount >= maxRetry)
        {
            throw Exception::Error;
        }
        else
        {
            retry;
        }
    }
}
