/// <summary>
/// Retrieves the resource that has been scheduled or assigned as the requirement for the current
/// record.
/// </summary>
/// <param name="_findFirstApplicable">
/// A Boolean value that indicates whether to get the first applicable resource for this record if no
/// resource has been assigned and no costing resource is found.
/// </param>
/// <param name="_searchFromDate">
/// The first date for which to search for resources; optional.
/// </param>
/// <param name="_searchToDate">
/// The last date for which to search for resources; optional.
/// </param>
/// <param name="_inventSiteId">
/// The ID of the site for which search for resources.
/// </param>
/// <param name="_findCostingResource">
/// A <c>Boolean</c> value that indicates whether to get the costing resource for this record.
/// </param>
/// <returns>
/// A <c>WrkCtrTable</c> record that satisfies the search criteria.
/// </returns>
public WrkCtrTable getResource(boolean _findFirstApplicable = false,
                               TransDate _searchFromDate = dateNull(),
                               TransDate _searchToDate = maxDate(),
                               InventSiteId _inventSiteId = '',
                               boolean _findCostingResource = false)
{
    WrkCtrTable wrkCtrTable;

    // Get the costing resource if none is found
    if (_findCostingResource && this.WrkCtrIdCost)
    {
        wrkCtrTable = WrkCtrTable::find(this.WrkCtrIdCost);
    }

    // get directly assigned resource
    if (!wrkCtrTable.RecId)
    {
        wrkCtrTable = this.activityRequirementSet().resourceRequirement();
    }

    // Get the first applicable resource if none is found
    if (!wrkCtrTable.RecId && _findFirstApplicable)
    {
        wrkCtrTable = this.activityRequirementSet().firstApplicableResource(_searchFromDate, _searchToDate, _inventSiteId);
    }

    return wrkCtrTable;
}

/// <summary>
/// Retrieves the resource group for a specific resource or resource group requirement.
/// </summary>
/// <param name="_searchFromDate">
/// The first date on which to search for resources.
/// </param>
/// <param name="_siteId">
/// The ID of the site on which to search for resources.
/// </param>
/// <returns>
/// A <c>WrkCtrResourceGroup</c> record that contains the group data.
/// </returns>
public WrkCtrResourceGroup getWrkCtrResourceGroup(TransDate _searchFromDate = dateNull(),
                                                  InventSiteId _siteId = this.SiteId)
{
    WrkCtrResourceGroup             resourceGroup;
    WrkCtrActivityRequirementSet    activityRequirementSet = this.activityRequirementSet();

    if (activityRequirementSet)
    {
        WrkCtrTable wrkCtrTable = activityRequirementSet.resourceRequirement();
        if (wrkCtrTable)
        {
            resourceGroup = WrkCtrTable::resourceGroup(wrkCtrTable.WrkCtrId);
        }

        if (!resourceGroup)
        {
            resourceGroup = activityRequirementSet.resourceGroupRequirement();
        }

        if (!resourceGroup)
        {
            resourceGroup = activityRequirementSet.firstApplicableResourceGroup(_searchFromDate, maxDate(), _siteId);
        }
    }

    return resourceGroup;
}

/// <summary>
/// Initializes this record from the values found in the <c>WrkCtrTable</c> table.
/// </summary>
/// <param name="_wrkCtrId">
/// The ID of a <c>WrkCtrTable</c> record to initialize from.
/// </param>
void initFromWrkCtrId(WrkCtrId _wrkCtrId)
{
    if (_wrkCtrId)
    {
        WrkCtrTable wrkCtrTable = WrkCtrTable::find(_wrkCtrId);

        if (wrkCtrTable)
        {
            if (wrkCtrTable.WrkCtrType== WrkCtrType::Vendor)
            {
                this.RouteType= RouteOprType::Vendor;
            }
            else
            {
                this.RouteType= RouteOprType::Standard;
            }

            if (wrkCtrTable.RouteGroupId)
            {
                this.RouteGroupId           = wrkCtrTable.RouteGroupId;
            }

            this.SetUpCategoryId        = wrkCtrTable.SetUpCategoryId;
            this.ProcessCategoryId      = wrkCtrTable.ProcessCategoryId;
            this.QtyCategoryId          = wrkCtrTable.QtyCategoryId;
            this.WrkCtrIdCost           = wrkCtrTable.WrkCtrId;

            if (wrkCtrTable.IsIndividualResource)
            {
                this.QueueTimeBefore        = wrkCtrTable.QueueTimeBefore;
                this.SetupTime              = wrkCtrTable.SetupTime;
                this.ProcessTime            = wrkCtrTable.ProcessTime;
                this.ProcessPerQty          = wrkCtrTable.ProcessPerQty;
                this.TranspTime             = wrkCtrTable.TranspTime;
                this.QueueTimeAfter         = wrkCtrTable.QueueTimeAfter;
                this.ToHours                = wrkCtrTable.ToHours;
                this.TransferBatch          = wrkCtrTable.TransferBatch;
            }
        }
    }
}

/// <summary>
///    Initializes the specified table buffer with default values.
/// </summary>
void initValue()
{
    super();

    this.RouteCode = RouteParameters::find().BlockRemoveApproval ? RouteAll::Route : RouteAll::All;

    this.ItemCode         = TableGroupAll::All;
    this.ToHours          = 1;
    this.ProcessPerQty    = 1;
    this.FormulaFactor1   = 1;
}

