public void insert()
{
    ttsbegin;

    if (this.RouteCode == RouteAll::Route)
    {
        if (!this.canEdit())
        {
            throw error("@SYS95153");
        }
    }

    this.updateRouteScrap();

    super();

    WrkCtrActivity                  wrkCtrActivity;
    WrkCtrRouteOprActivity          wrkCtrRouteOprActivity;
    WrkCtrActivityRequirementSet    wrkCtrActivityRequirementSet;

    //Add activity
    wrkCtrActivity.EntityType = WrkCtrActivityType::RouteOpr;
    wrkCtrActivity.insert();

    wrkCtrRouteOprActivity.Activity = wrkCtrActivity.RecId;
    wrkCtrRouteOprActivity.RouteOpr = this.RecId;
    wrkCtrRouteOprActivity.RouteOprDataAreaId = this.DataAreaId;
    wrkCtrRouteOprActivity.insert();

    //Add requirement set
    wrkCtrActivityRequirementSet.initValue();
    wrkCtrActivityRequirementSet.Activity = wrkCtrActivity.RecId;
    wrkCtrActivityRequirementSet.insert();

    ttscommit;
}

public void modifiedFieldValue(FieldName _fieldName, int _arrayIndex = 1)
{
    super(_fieldName, _arrayIndex);

    if (_fieldName == fieldStr(RouteOpr, WrkCtrIdCost))
    {
        if (FormDataUtil::isFormDataSource(this)
            && this.WrkCtrIdCost
            && this.WrkCtrIdCost != this.orig().WrkCtrIdCost
            && Box::yesNo(strFmt("@SYS301004",this.WrkCtrIdCost),DialogButton::Yes) == DialogButton::Yes)
        {
            this.initFromWrkCtrId(this.WrkCtrIdCost);
            FormDataUtil::getFormDataSource(this).refresh();
        }
    }
}

