void promptUpdateProcessTime(WrkCtrRequirementQuantity _origQuantity,
                                    WrkCtrRequirementQuantity _newQuantity )
{
    if (this)
    {
        real newProcessTime = this.ProcessTime * _origQuantity / _newQuantity;

        if (Box::yesNo(strFmt("@SYS23350",this.ProcessTime,newProcessTime),DialogButton::Yes) == 1)
        {
            this.ProcessTime = newProcessTime;
        }
    }
}

