public void update()
{
    ttsbegin;

    if (this.orig().RouteCode == RouteAll::Route)
    {
        if (!this.canEdit())
        {
            throw error("@SYS95153");
        }
    }

    super();

    this.updateRouteScrap();

    ttscommit;
}

public void updateRouteScrap()
{
    if (this.RouteCode != RouteAll::Route)
    {
        return;
    }

    WrkCtrTable     wrkCtrTable = this.getResource();
    Route           route;
    FormRun         formRun;
    FormDataSource  routeOpr_ds;
    FormDataSource  route_ds;

    while select forupdate route
        where route.RouteId          == this.RouteRelation          &&
              route.OprId            == this.OprId                  &&
              route.OprPriority      == RouteOprPriority::Primary   &&
              route.ErrorPct         <  wrkCtrTable.ErrorPct
    {
        route.ErrorPct = wrkCtrTable.ErrorPct;
        route.update();
        if (FormDataUtil::isFormDataSource(this))
        {
            routeOpr_ds = FormDataUtil::getFormDataSource(this);
            formRun = routeOpr_ds.formRun();
            if (formRun && routeOpr_ds.joinSource())
            {
                route_ds = formGetParentDatasource(routeOpr_ds);
                if (route_ds)
                {
                    route_ds.reread();
                    route_ds.refresh();
                }
            }
        }
    }
}

public boolean validateDelete()
{
    boolean ret = super();

    if (ret && FormDataUtil::getFormDataSource(this) && !this.orig().canEdit())
    {
        ret = checkFailed("@SYS95153");
    }

    return ret;
}

public boolean validateFieldValue(FieldName _fieldName, int _arrayIndex = 1)
{
    boolean ret = super(_fieldName, _arrayIndex);

    switch (_fieldName)
    {
        case fieldStr(RouteOpr, RouteCode):
            if (!this.isRouteCodeValid())
            {
                ret = checkFailed("@SYS95154");
            }
            break;

        case fieldStr(RouteOpr, ItemRelation):
            if (!this.isItemRelationValid())
            {
                ret = checkFailed("@SYS301853");
            }
            break;

        default:
    }

    return ret;
}

/// <summary>
/// Checks if the RouteCode field is valid.
/// </summary>
/// <returns>true if the RouteCode field is valid; otherwise, false.</returns>
protected boolean isRouteCodeValid()
{
    if (this.RouteCode == RouteAll::All)
    {
        return !RouteParameters::find().BlockRemoveApproval;
    }

    return true;
}

/// <summary>
/// Checks if the ItemRelation field is valid.
/// </summary>
/// <returns>true if the ItemRelation field is valid; otherwise, false.</returns>
protected boolean isItemRelationValid()
{
    if (this.ItemCode == TableGroupAll::Table)
    {
        return InventTable::find(this.ItemRelation).isStocked();
    }

    return true;
}

boolean validateWrite()
{
    boolean ok = super();

    if (ok && FormDataUtil::getFormDataSource(this) && !this.orig().canEdit())
    {
        ok = checkFailed("@SYS95153");
    }

    if (! this.RouteOprMap::checkFields())
    {
        ok = false;
    }

    if (this.ItemCode == TableGroupAll::Table)
    {
        ok = EcoResProductVariantDimValue::newProductVariantDim_ConfigId().checkExist(
            InventTable::itemProduct(this.ItemRelation), this.ConfigId) && ok;
    }

    return ok;
}

/// <summary>
/// Opens the primary form for the selected record.
/// </summary>
/// <param name="_routeOprRecId">
/// The record ID.
/// </param>
/// <remarks>
/// The method is not run if the record does not exist.
/// </remarks>
internal static void jumpRefRouteOpr(RecId _routeOprRecId)
{
    RouteOpr routeOpr;

    if (_routeOprRecId)
    {
        routeOpr = RouteOpr::findRecId(_routeOprRecId);
    }

    if (!routeOpr)
    {
        return;
    }

    Args args = new Args();
    args.record(routeOpr);

    FormRun formRun = new MenuFunction(menuitemdisplaystr(RouteOpr), MenuItemType::Display).create(args);
    formRun.run();
    formRun.dataSource().findRecord(routeOpr);
    formRun.detach();
}

static RouteOpr find( RouteOprId _oprId, ItemCode _itemCode, ItemRelationInvent _itemRelation, EcoResItemConfigurationName _configId, RouteAll _routeCode, RouteRelation _routeRelation, InventSiteId _siteId, boolean _forUpdate = false ) { RouteOpr routeOpr; routeOpr.selectForUpdate(_forUpdate);
select routeOpr index hint OprRelIdx where routeOpr.OprId == _oprId && routeOpr.ItemCode == _itemCode && routeOpr.ItemRelation == _itemRelation && routeOpr.ConfigId == _configId && routeOpr.RouteCode == _routeCode && routeOpr.RouteRelation == _routeRelation && routeOpr.SiteId == _siteId;
return routeOpr; }

static RouteOpr findRecId(RecId _recId, boolean _forUpdate = false)
{
    RouteOpr routeOpr;

    if (!_recId)
    {
        return routeOpr;
    }

    routeOpr.selectForUpdate(_forUpdate);

    select routeOpr
        where routeOpr.RecId== _recId;

    return routeOpr;
}
