/// <summary>
/// Checks whether any fields in the <c>SalesAgreamentHeaderDefault</c> table have nondefault values.
/// </summary>
/// <returns>
/// true if all fields have default values; otherwise, false.
/// </returns>
/// <remarks>
/// The method is used during insert and update to make sure that the
/// <c>SalesAgreamentHeaderDefault</c> record should be saved.If the record only contains default
/// values it should not be saved. For more information; see the <c>insert</c> and <c>update</c>
/// methods.
/// </remarks>
private boolean checkDefaultValues()
{
    if ( this.CommissionCustomerGroup
        || this.CommissionSalesGroup
        || this.CustomerMethodOfPayment
        || this.CustomerPaymentSpec
        || this.CustomerRequisitionNumber
        || this.FixedExchangeRate
        || this.ReportingCurrencyFixedExchRate
        || this.CustomerInvoiceAccount
        || this.SalesPool
        || this.WorkerSalesResponsible
        || this.SalesUnit
        || this.Campaign
       )
    {
        return false;
    }

    return true;
}

/// <summary>
/// Gets the name of the customer invoice account for the default record of the current sales agreement
/// header.
/// </summary>
/// <returns>
/// A string that contains the name of the customer.
/// </returns>
public display CustName custInvoiceAccountName()
{
    CustTable       custTable;
    DirPartyTable   partyTable;

    if (this.CustomerInvoiceAccount)
    {
        select firstonly
               crossCompany
               Party from custTable
                where    custTable.AccountNum == this.CustomerInvoiceAccount
                      && custTable.DataAreaId == this.CustomerInvoiceAccountDataAreaId
            join Name from partyTable
                where partyTable.RecId == custTable.Party;
    }
    return partyTable.Name;
}

/// <summary>
///    Initializes the <c>SalesAgreementHeaderDefault</c> record from the <c>CustTable</c> record.
/// </summary>
/// <param name="_custTable">
///    The <c>CustTable</c> record from which to initialize.
/// </param>
public void initFromCustTable(CustTable _custTable)
{
    // <GEERU>
    #ISOCountryRegionCodes
    // </GEERU>
    this.CustomerInvoiceAccount            = _custTable.InvoiceAccount != ''? _custTable.InvoiceAccount : _custTable.AccountNum;
    this.CustomerInvoiceAccountDataAreaId  = appl.company().dataArea(tableNum(CustTable));

    this.CustomerMethodOfPayment           = _custTable.PaymMode;
    this.CustomerMethodOfPaymentDataAreaId = appl.company().dataArea(tableNum(CustPaymModeTable));
    this.CustomerPaymentSpec               = _custTable.PaymSpec;
    this.CustomerPaymentSpecDataAreaId     = appl.company().dataArea(tableNum(CustPaymModeSpec));

    this.SalesPool                         = _custTable.SalesPoolId;
    if (!this.SalesPool)
    {
        this.SalesPool                     = SalesParameters::find().SalesPoolId;
    }
    this.SalesPoolDataAreaId               = appl.company().dataArea(tableNum(SalesPool));

    this.CommissionCustomerGroup           = _custTable.CommissionGroup;
    this.CommissionCustomerGroupDataAreaId = appl.company().dataArea(tableNum(CommissionCustomerGroup));
    this.CommissionSalesGroup              = _custTable.SalesGroup;
    this.CommissionSalesGroupDataAreaId    = appl.company().dataArea(tableNum(CommissionSalesGroup));
    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        this.initFromCustTable_RU(_custTable);
    }
    // </GEERU>

    this.EntryCertificateRequired_W        = _custTable.EntryCertificateRequired_W;
    this.IssueOwnEntryCertificate_W        = _custTable.IssueOwnEntryCertificate_W;
}

/// <summary>
///    Initializes the <c>SalesAgreementHeaderDefault</c> record from the <c>CustTable</c> record.
/// </summary>
/// <param name="_custTable">
///    The <c>CustTable</c> record from which to initialize.
/// </param>
public void initFromCustTable_RU(CustTable _custTable)
{
    if (!this.CustomerInvoiceAccount)
    {
        this.CustomerInvoiceAccount = _custTable.AccountNum;
    }
}

/// <summary>
/// Inserts the records into the <c>SalesAgreementHeaderDefault</c> table.
/// </summary>
/// <remarks>
/// For more information see; the <see cref="SalesAgreementHeaderDefault" /> table.
