void  insert()
{
    if (!this.checkDefaultValues())
    {
        this.doInsert();
    }
}

/// <summary>
/// Retrieves the invoice <c>CustTable</c> record for the current record.
/// </summary>
/// <returns>
/// The <c>CustTable</c> record.
/// </returns>
public CustTable invoiceCustTable()
{
    return CustTable::findByCompany(this.CustomerInvoiceAccountDataAreaId, this.CustomerInvoiceAccount);
}

/// <summary>
/// Processes the modified field event for a record.
/// </summary>
/// <param name="_fieldId">
/// The field ID for the modified field.
/// </param>
public void modifiedField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldnum(SalesAgreementHeaderDefault, EntryCertificateRequired_W) :
            if (this.EntryCertificateRequired_W == NoYes::No)
            {
                this.IssueOwnEntryCertificate_W = NoYes::No;
            }
            break;

        case fieldNum(SalesAgreementHeaderDefault, CustomerMethodOfPayment):
            if (CFMParameters::isPaymentRequestsEnabled() && !CFMAgreementPaymentOrder::isMethodOfPaymentRu(this, this.CustomerMethodOfPayment))
            {
                warning("@CashFlowManagement:PaymentOrderRequisitesWillBeCleared");
            }
            break;
        default:
            break;
    }
}

/// <summary>
/// Updates the record in the <c>SalesAgreementHeaderDefault</c> table.
/// </summary>
/// <remarks>
/// The <c>SalesAgreementHeaderDefault</c> record should only be saved if a field has a nondefault
/// value.Indicates whether the record can actually be deleted or inserted during update.For more
/// information see; the <see cref="SalesAgreementHeaderDefault " /> table.
/// </remarks>
public void update()
{
    if (!this.checkDefaultValues())
    {
        if (this.RecId)
        {
            this.doUpdate();
        }
        else
        {
            this.doInsert();
        }
    }
    else    //all values are default - delete the record
    {
        if (this.RecId)
        {
            this.delete();
        }
    }
}

/// <summary>
///   Creates an <c>SalesAgreementHeaderDefault</c> record from an purchase agreement header record ID.
/// </summary>
/// <param name="_purchaseAgreementHeaderRecId">
///    The record ID  for the purchase agreement header to create the <c>SalesAgreementHeaderDefault</c> record from.
/// </param>
/// <param name="_isIntraCompany_RU">
///    The intracompany flag.
/// </param>
/// <remarks>
///    Used in intercompany scenarios.
/// </remarks>
public static void createFromPurchaseAgreementHeaderRecId(AgreementHeaderRecId _purchaseAgreementHeaderRecId
                                                          , boolean             _isIntraCompany_RU = false
)
{
    CustTable                           custTable;
    VendTable                           vendTable;
    SalesAgreementHeader                salesAgreementHeader;
    PurchAgreementHeader                purchAgreementHeader;
    SalesAgreementHeaderDefault         salesAgreementHeaderDefault;
    dataAreaId                          customerDataAreaId;
    // <GEERU>
    #ISOCountryRegionCodes
    boolean countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    // </GEERU>

    purchAgreementHeader = PurchAgreementHeader::find(_purchaseAgreementHeaderRecId);

    vendTable = purchAgreementHeader.vendTable();
    if (vendTable.RecId)
    {
        // <GEERU>
        if (countryRegion_RU && _isIntraCompany_RU)
        {
            customerDataAreaId = vendTable.company();
        }
        else
        {
        // </GEERU>
            customerDataAreaId = vendTable.interCompanyTradingPartnerCompanyID();
        // <GEERU>
        }
        // </GEERU>

        if (!TradeInterCompany::checkDataAreaAccess(customerDataAreaId))
        {
            return;
        }

        changecompany(customerDataAreaId)
        {
            // <GEERU>
            if (countryRegion_RU && _isIntraCompany_RU)
            {
                salesAgreementHeader = purchAgreementHeader.salesAgreementHeader_RU();
            }
            else
            {
            // </GEERU>
                salesAgreementHeader = purchAgreementHeader.salesAgreementHeader();
            // <GEERU>
            }
            // </GEERU>
            select firstonly salesAgreementHeaderDefault
                where salesAgreementHeaderDefault.SalesAgreementHeader == salesAgreementHeader.RecId;
            if (!salesAgreementHeaderDefault.RecId)
            {
                // <GEERU>
                if (countryRegion_RU && _isIntraCompany_RU)
                {
                    custTable = CustTable::findByPartyRecId(vendTable.Party);
                }
                else
                {
                // </GEERU>
                    custTable = CustTable::find(vendTable.interCompanyTradingPartnerAccount());
                // <GEERU>
                }
                // </GEERU>
                salesAgreementHeaderDefault.SalesAgreementHeader = salesAgreementHeader.RecId;
                salesAgreementHeaderDefault.initFromCustTable(custTable);
                salesAgreementHeaderDefault.insert();
            }
        }
    }
}

/// <summary>
///    Finds the specified record in the <c>SalesAgreementHeaderDefault</c> table.
/// </summary>
/// <param name="_agreementHeaderRecId">
///    The record ID of the <c>SalesAgreementHeader</c> record to find.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
///    A record in the <c>SalesAgreementHeaderDefault</c> table; otherwise, an empty record.
/// </returns>
public static SalesAgreementHeaderDefault findSalesAgreementHeader(AgreementHeaderRecId  _agreementHeaderRecId,
                                                                   boolean   _forUpdate = false)
{
    SalesAgreementHeaderDefault salesAgreementHeaderDefault;

    if (_agreementHeaderRecId)
    {
        if (_forUpdate)
        {
            salesAgreementHeaderDefault.selectForUpdate(_forUpdate);
        }

        select firstonly salesAgreementHeaderDefault
            where salesAgreementHeaderDefault.SalesAgreementHeader == _agreementHeaderRecId;
    }

    return salesAgreementHeaderDefault;
}

/// <summary>
/// Gets and sets the fixed exchange rate.
/// </summary>
/// <param name = "set">A boolean value that indicates whether the fixed exchange rate is set.</param>
/// <param name = "_exchRate">The new fixed exchange rate when the <paramref name="set" /> value is true.</param>
/// <returns>The fixed exchange rate of the record.</returns>
internal edit SalesFixedExchRate editFixedExchangeRate(boolean set, SalesFixedExchRate _exchRate)
{
    ExchangeRateHelper exchangeRateHelper = this.getExchangeRateHelper();

    if (set)
    {
        this.FixedExchangeRate = exchangeRateHelper.prepareExchangeRateForStorage(_exchRate);
        this.setReportingCurrencyFixedExchangeRate();
    }
    else
    {
        _exchRate = exchangeRateHelper.displayStoredExchangeRate(this.FixedExchangeRate);
    }

    return _exchRate;
}

