void convertCurrencyCode(CurrencyCode            _currencyCode,
                                TradeCurencyConversion  _tradeCurrencyConversion = TradeCurencyConversion::ExchRate)
{
    SalesBasketLine       salesBasketLine;

    ttsbegin;

    while select forupdate salesBasketLine
          where salesBasketLine.SalesBasketId == this.SalesBasketId
    {
        salesBasketLine.convertCurrencyCode(_currencyCode, _tradeCurrencyConversion);
        salesBasketLine.doUpdate();
    }

    this.CurrencyCode = _currencyCode;

    ttscommit;
}

