public SalesShippingDate deliveryDate(SalesShippingDate _blankDate = dateNull())
{
    if (this.DeliveryDate)
    {
        return this.DeliveryDate;
    }

    if (prmisdefault(_blankDate))
    {
        return InventOrderEntryDeadlineTable::effectiveOrderDate('','');
    }

    return _blankDate;
}

