public boolean validateField(fieldId _fieldIdToCheck)
{
    boolean ret;

    ret = super(_fieldIdToCheck);

    if (ret)
    {
        switch (_fieldIdToCheck)
        {
            case fieldnum(SalesBasket, DeliveryDate) :
                if (this.DeliveryDate < DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()))
                    ret = checkFailed(strfmt("@SYS88286", date2StrUsr(this.DeliveryDate, DateFlags::FormatAll)));
                break;

            case fieldnum(SalesBasket,InventLocationId) :
                ret = this.InventStorageDimMap::validateField(fieldnum(InventStorageDimMap,InventLocationId));
                break;
        }
    }

    return ret;
}

public void write()
{
    this.updateMultiLineDisc();
    this.updateFinalDisc();

    super();
}

static SalesBasket find(SalesBasketId salesBasketId,
                        boolean       _forUpdate = false)
{
    SalesBasket salesBasket;

    if (salesBasketId)
    {
        salesBasket.selectForUpdate(_forUpdate);

        select firstonly salesBasket
            where salesBasket.SalesBasketId == salesBasketId;
    }
    return salesBasket;
}

static SalesBasket findRecId(recId       recId,
                             boolean     _forUpdate = false )
{
    SalesBasket salesBasket;

    salesBasket.selectForUpdate(_forUpdate);

    select salesBasket
        where salesBasket.RecId == recId;

    return salesBasket;
}

static void updateDisc(SalesBasketId   salesBasketId, boolean forceCalc = false)
{
    SalesBasket     salesBasket;
    SalesBasketLine salesBasketLine;

    if (salesBasketId)
    {
        ttsbegin;
        salesBasket = SalesBasket::find(salesBasketId,true);

        // Update cached salesBasket record with any changes made on customer record
        salesBasket.initFromCustTable();

        if (salesBasket.PriceCalc != DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()) || forceCalc)
        {
            if (!forceCalc)
            {
                while select forupdate * from salesBasketLine where salesBasketLine.SalesBasketId == salesBasket.SalesBasketId
                {
                    salesBasketLine.salesPurchLineInterface().resetPriceAgreement();
                    salesBasketLine.setPriceDisc(salesBasketLine.inventDim());
                    salesBasketLine.update();
                }
            }
            salesBasket.updateFinalDisc();
            salesBasket.updateMultiLineDisc();
            salesBasket.PriceCalc = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
        }

        salesBasket.update();
        ttscommit;
    }
}

