/// <summary>
/// Determines facture module depending on current record transaction type
/// </summary>
/// <returns>
/// <c>FactureModule_RU</c> value
/// </returns>
public FactureModule_RU factureModule()
{
    FactureModule_RU    ret;

    switch (this.TransType)
    {
        case SalesPurchBookTransType_RU::PrepaymentStorno :
            ret = FactureModule_RU::Vend;
            break;
        case SalesPurchBookTransType_RU::TaxCorrection    :
            ret = FactureJour_RU::findTaxCorrection(this.FactureId, FactureBook_RU::Sales).Module;
            break;
        case SalesPurchBookTransType_RU::VATRestoring     :
            ret = FactureModule_RU::Vend;
            break;
        default                                           :
            ret = FactureModule_RU::Cust;
    }

    return ret;
}

public void insert()
{
    Debug::assert(this.FactureJour_RU != 0);
    this.SalesPurchBookDataMap_RU::initRefOriginalFacture();
    this.SalesPurchBookDataMap_RU::createAnnulment();

    if (this.AmountInclVAT || this.AmountLiableToVAT0 || this.AmountLiableToVAT10 ||
        this.AmountLiableToVAT20 || this.AmountNotLiableToVAT || this.TaxAmountVAT10 ||
        this.TaxAmountVAT20 || this.AmountCurInclVAT)
    {
        super();
    }
}

