/// <summary>
///  Returns string which contains currency ISO Code and string code.
/// </summary>
/// <param name="_currencyCode">
/// currency code to find  the ISO code
/// </param>
/// <returns>
///  String value.
/// </returns>
display public Description currencyNameCode(CurrencyCode _currencyCode = this.CurrencyCode)
{
    Description     ret;
    Currency        currency;
    ISOCurrencyCode isoCurrencyCode;

    if (_currencyCode != CompanyInfoHelper::standardCurrency())
    {
        currency = Currency::find(_currencyCode);
        isoCurrencyCode = ISOCurrencyCode::find(currency.CurrencyCodeISO);

        ret = strFmt("@SYS26868", currency.Txt, isoCurrencyCode.ISOCurrencyCodeNum);
    }

    return ret;
}

/// <summary>
///  Returns string, which contains payment document number and date.
/// </summary>
/// <returns>
///  String value.
/// </returns>
display public Description paymNumDate()
{
    FactureJour_RU factureJour;
    boolean        printDocNum;
    Description    ret;
    if (this.PaymDocumentNum && this.PaymentDate)
    {
        switch (this.TransType)
        {
            case SalesPurchBookTransType_RU::TaxCorrection:
                factureJour = FactureJour_RU::find(this.FactureId, this.factureModule());

                if (factureJour.Module == FactureModule_RU::TaxCorrectionSales_Vend && factureJour.VATTaxAgent)
                {
                    printDocNum = true;
                }
                break;
            case SalesPurchBookTransType_RU::Prepayment:
            case SalesPurchBookTransType_RU::PrepaymentStorno:
                printDocNum = true;
                break;
        }

        if (printDocNum)
        {
            ret = strFmt("@GLS102243", this.PaymDocumentNum, this.PaymentDate);
        }
    }
    return ret;
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;

    switch (_fieldIdToCheck)
    {
        case fieldnum(PurchBookTrans_RU, OperationTypeCodes):
            ret = OperationTypeList_RU::checkOperationTypeCodes(this.OperationTypeCodes);
            break;
        default:
            ret = super(_fieldIdToCheck);
    }

    return ret;
}

/// <summary>
/// Differentiates standard VAT amounts by VAT rate.
/// </summary>
/// <returns>
/// A container with VAT base / amount pairs for VAT rates
/// </returns>
public container amountsVATStandard()
{        
    #SalesBook_RU
    AmountMST amountLiableToVAT20, taxAmountVAT20,
        amountLiableToVAT18, taxAmountVAT18;

    if (this.AmountLiableToVAT20 || this.TaxAmountVAT20)
    {
        FactureTrans_RU factureTrans;
        select firstonly factureTrans
            where factureTrans.FactureId == this.FactureId
                && factureTrans.Module == this.factureModule()
                && factureTrans.VATType == VatType_RU::Full;

        if (factureTrans.VATValue == #vatValue20)
        {
            amountLiableToVAT20 = this.AmountLiableToVAT20;
            taxAmountVAT20 = this.TaxAmountVAT20;
        }
        else
        {
            amountLiableToVAT18 = this.AmountLiableToVAT20;
            taxAmountVAT18 = this.TaxAmountVAT20;
        }
    }

    return [ amountLiableToVAT20, taxAmountVAT20,
        amountLiableToVAT18, taxAmountVAT18 ];
}

/// <summary>
/// Determines if concurrent deletes should throw exception.
/// </summary>
/// <returns>true if exception should be thrown; otherwise false.</returns>
public boolean shouldThrowExceptionOnZeroDelete()
{
    return super() || SalesEnforceConcurrentDeletesFlight::instance().isEnabled();
}
