public display FactureDate_External_RU factureDate_External()
{
    FactureJour_RU factureJour;

    switch (this.TransType)
    {
        case SalesPurchBookTransType_RU::TaxCorrection :
            select firstonly FactureDate_External from factureJour
                where  factureJour.FactureId == this.FactureId                       &&
                      (factureJour.Module    == FactureModule_RU::TaxCorrectionSales ||
                       factureJour.Module    == FactureModule_RU::TaxCorrectionSales_Vend);
            break;

        default                                        :
            select firstonly FactureDate_External from factureJour
                where factureJour.FactureId == this.FactureId &&
                      factureJour.Module    == this.factureModule();
    }

    return factureJour.FactureDate_External;
}

public FactureModule_RU factureModule()
{
    return SalesBookVATProcessLogTrans_RU::transType2FactureModule(this.TransType, this.FactureId, this.FactureDate, this.InvoiceRecIdRef);
}

