public static SalesBookProcessingType_RU factureType2type(SalesBookFactureProcessingType_RU _factureProcessingType)
{
    SalesBookProcessingType_RU processingType;

    switch (_factureProcessingType)
    {
        case SalesBookFactureProcessingType_RU::VATZero :
            processingType = SalesBookProcessingType_RU::VATZero;
            break;

        case SalesBookFactureProcessingType_RU::VATStandard :
            processingType = SalesBookProcessingType_RU::VATStandard;
            break;

        case SalesBookFactureProcessingType_RU::VATZeroCorrection :
            processingType = SalesBookProcessingType_RU::VATZeroCorrection;
            break;

        case SalesBookFactureProcessingType_RU::VATStandardCorrection :
            processingType = SalesBookProcessingType_RU::VATStandardCorrection;
            break;

        case SalesBookFactureProcessingType_RU::OverdueConfirmation :
            processingType = SalesBookProcessingType_RU::OverdueConfirmation;
            break;

        case SalesBookFactureProcessingType_RU::VATStandardCancellation :
            processingType = SalesBookProcessingType_RU::VATStandardCancellation;
            break;

        case SalesBookFactureProcessingType_RU::None :
            processingType = SalesBookProcessingType_RU::VATRestoration;
            break;

        default :
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    return processingType;
}

public static SalesBookVATProcessLogTrans_RU find(RecId _recId, boolean _forUpdate = false)
{
    SalesBookVATProcessLogTrans_RU  salesBookVATProcessLogTrans;

    salesBookVATProcessLogTrans.selectForUpdate(_forUpdate);

    if (_recId)
    {
        select firstonly salesBookVATProcessLogTrans
            where salesBookVATProcessLogTrans.RecId == _recId;
    }
    return salesBookVATProcessLogTrans;
}

public static RecordSortedList findFromSettlement(FactureId_RU _factureId,
                                                         RecId        _transRecId,
                                                         RecId        _offsetRecId,
                                                         CompanyId    _transCompany,
                                                         CompanyId    _offsetCompany,
                                                         RecId        _settlementRecId,
                                                         TransDate    _settlementDate,
                                                         SalesBookProcessingType_RU _processingType,
                                                         boolean      _forUpdate = false)
{
    SalesBookVATProcessLogTrans_RU  processLogTrans;
    RecordSortedList                processLogTransList;

    processLogTrans.selectForUpdate(_forUpdate);

    processLogTransList = new RecordSortedList(tableNum(SalesBookVATProcessLogTrans_RU));
    processLogTransList.sortOrder(fieldNum(SalesBookVATProcessLogTrans_RU, RecId));

    select firstonly processLogTrans
        where processLogTrans.SettlementRecId == _settlementRecId &&
              processLogTrans.FactureId       == _factureId       &&
              processLogTrans.InvoiceRecIdRef == _transRecId      &&
              processLogTrans.PaymentRecIdRef == _offsetRecId     &&
              processLogTrans.InvoiceCompany  == _transCompany    &&
              processLogTrans.PaymentCompany  == _offsetCompany   &&
              processLogTrans.SettlementDate  == _settlementDate  &&
              processLogTrans.ProcessingType  == _processingType;

    if (processLogTrans)
    {
        processLogTransList.ins(processLogTrans);
    }

    return processLogTransList;
}

public static BookStructVATAmounts_RU getLogAmounts(RecId   _logTransRecId,
                                                           RecId   _logTableRecId,
                                                           boolean _inclCanceledOperations = true,
                                                           boolean _inclBlockedOperations  = true)
{
    SalesBookVATProcessLogTransOper_RU logTransOper;
    BookStructVATAmounts_RU            processedAmounts = new BookStructVATAmounts_RU();
    BookStructSmallVATAmounts_RU       bookStructSmallVATAmounts;

    if (_inclCanceledOperations)
    {
        if (_inclBlockedOperations)
        {
            select logTransOper
                where logTransOper.LogTableRefRecId == _logTableRecId &&
                      logTransOper.RefRecId         == _logTransRecId &&
                      logTransOper.Correct          == NoYes::No;
        }
        else
        {
            select logTransOper
                where logTransOper.LogTableRefRecId == _logTableRecId &&
                      logTransOper.RefRecId         == _logTransRecId &&
                      logTransOper.Blocked          == NoYes::No      &&
                      logTransOper.Correct          == NoYes::No;
        }
    }
    else
    {
        if (_inclBlockedOperations)
        {
            select logTransOper
                where logTransOper.LogTableRefRecId == _logTableRecId &&
                      logTransOper.RefRecId         == _logTransRecId;
        }
        else
        {
            select logTransOper
                where logTransOper.LogTableRefRecId == _logTableRecId &&
                      logTransOper.RefRecId         == _logTransRecId &&
                      logTransOper.Blocked          == NoYes::No;
        }
    }

    while (logTransOper)
    {
        if (! processedAmounts.isGroupBy())
        {
            bookStructSmallVATAmounts = BookStructSmallVATAmounts_RU::create(logTransOper.FactureAmounts);
            processedAmounts.isGroupBy(bookStructSmallVATAmounts.qty() || bookStructSmallVATAmounts.itemName());
        }
        processedAmounts.addSmallAmounts(logTransOper.TaxCode, logTransOper.amountInclVAT(), logTransOper.AmountLiableToVAT, logTransOper.TaxAmount);
        next logTransOper;
    }

    return processedAmounts;
}

static public FactureModule_RU transType2FactureModule(SalesPurchBookTransType_RU   _transType,
                                                       FactureId_RU                 _factureId,
                                                       FactureDate_RU               _factureDate,
                                                       RecId                        _invoiceRecId)
{
    FactureModule_RU    ret;

    switch (_transType)
    {
        case SalesPurchBookTransType_RU::CreditNote        :
        case SalesPurchBookTransType_RU::Invoice           :
        case SalesPurchBookTransType_RU::SumDiff           :
        case SalesPurchBookTransType_RU::ExchAdj           :
        case SalesPurchBookTransType_RU::AdvanceAdjustment :
        case SalesPurchBookTransType_RU::Prepayment        :
            ret = FactureModule_RU::Cust;
            break;

        case SalesPurchBookTransType_RU::TaxCorrection     :
            ret = FactureJour_RU::findTaxCorrection(_factureId, FactureBook_RU::Sales).Module;
            break;

        case SalesPurchBookTransType_RU::PrepaymentStorno  :
            ret = FactureModule_RU::Vend;
            break;

        case SalesPurchBookTransType_RU::VATRestoring      :
            ret = RestoredVATLogTrans_RU::findFacture(_factureId, _factureDate, _invoiceRecId).FactureModule;
            break;
    }

    return ret;
}

public static SalesBookFactureProcessingType_RU type2factureType(SalesBookProcessingType_RU _processingType)
{
    SalesBookFactureProcessingType_RU factureProcessingType;

    switch (_processingType)
    {
        case SalesBookProcessingType_RU::VATZero :
            factureProcessingType = SalesBookFactureProcessingType_RU::VATZero;
            break;

        case SalesBookProcessingType_RU::VATStandard :
            factureProcessingType = SalesBookFactureProcessingType_RU::VATStandard;
            break;

        case SalesBookProcessingType_RU::VATZeroCorrection :
            factureProcessingType = SalesBookFactureProcessingType_RU::VATZeroCorrection;
            break;

        case SalesBookProcessingType_RU::VATStandardCorrection :
            factureProcessingType = SalesBookFactureProcessingType_RU::VATStandardCorrection;
            break;

        case SalesBookProcessingType_RU::OverdueConfirmation :
            factureProcessingType = SalesBookFactureProcessingType_RU::OverdueConfirmation;
            break;

        case SalesBookProcessingType_RU::VATStandardCancellation :
            factureProcessingType = SalesBookFactureProcessingType_RU::VATStandardCancellation;
            break;

        default :
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    return factureProcessingType;
}

/// <summary>
/// Determines if concurrent deletes should throw exception.
/// </summary>
/// <returns>true if exception should be thrown; otherwise false.</returns>
public boolean shouldThrowExceptionOnZeroDelete()
{
    return super() || SalesEnforceConcurrentDeletesFlight::instance().isEnabled();
}
