void initValue()
{
    const int DefaultOrderBatchTaskSizeLines = 0;
    const int DefaultOrderBatchTaskSize = 5;
    const int DefaultQuotationExpiryDate = 30;
    const int DefaultQuotationFollowupdays = 2;

    // Green check box resource, displayed for acceptable price margin
    #define.MCRResourceNum_Acceptable(3125)
    // Yellow check box resource, displayed for questionable price margin
    #define.MCRResourceNum_Questionable(3127)
    // Red check box resource, displayed for unacceptable price margin
    #define.MCRResourceNum_Unacceptable(3126)
    super();

    this.SplitSitePickingList           = NoYes::Yes;
    this.SplitSitePackingSlip           = NoYes::Yes;
    this.SplitTaxIdInvoice              = NoYes::Yes;
    this.SplitTaxIdPackingSlip          = NoYes::Yes;

    this.QuotationNumOfDaysExpiryDate   = DefaultQuotationExpiryDate;
    this.QuotationNumOfDaysFollowUpDate = DefaultQuotationFollowupdays;

    this.PromptQty = NoYes::Yes;
    this.OrderBatchTaskSize = DefaultOrderBatchTaskSize;
    this.OrderBatchTaskSizeLines = DefaultOrderBatchTaskSizeLines;
    this.DecimalPrecisionCheckQty = NoYes::Yes;

    if (SalesReplaceAltItemDataFeature::instance().isEnabled())
    {
        this.ReplaceAlternativeItemLineDefaults = NoYes::Yes;
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        this.SplitSiteInvoice = true;
    }
    // </GBR>

    if (SalesD365SalesMessageProcessorParameterSetupFlight::instance().isEnabled() &&
        SalesD365SalesMessageProcessorParameterSetupTimeoutFlight::instance().isEnabled())
    {
        this.MessageProcessorBatchTaskSize = SalesD365SalesMessageProcessorParameterSetup::DefaultBatchTaskSize;
        this.MessageProcessorCreateQuotationJournalAsync = SalesD365SalesMessageProcessorParameterSetup::DefaultAsyncProcessingEnabled;
        this.MessageProcessorCreateQuotationConfirmJournalAsync = SalesD365SalesMessageProcessorParameterSetup::DefaultAsyncProcessingEnabled;
        this.MessageProcessorQuotationDefaultSalesOrderAsync = SalesD365SalesMessageProcessorParameterSetup::DefaultAsyncProcessingEnabled;
        this.MessageProcessorQuotationDefaultSalesOrderLinesPerMessage = SalesD365SalesMessageProcessorParameterSetup::DefaultBatchTaskSize;
        this.MessageProcessorCalculateSalesPricesAndTotalsAsync = SalesD365SalesMessageProcessorParameterSetup::DefaultAsyncProcessingEnabled;
        this.MessageProcessorCalculateSalesPricesAndTotalsLinesPerMessage = SalesD365SalesMessageProcessorParameterSetup::DefaultBatchTaskSize;
    }

    if (SalesDataEntityExpPolicyFeature::instance().isEnabled())
    {
        this.SkipReferencedDataDuringChangeTracking = NoYes::Yes;
        this.SkipSalesQuotationReferencedDataDuringChangeTracking = NoYes::Yes;
    }
}

